var _swdoc = (IsObjectDefined("_bHtmlWebClient"))?top:_swdoc;
var webclient =IsObjectDefined("_bHtmlWebClient");
var strSelectedKeys= "";
var strSelectedText = "";
var strSelectedOther = "";
var strSelectedCMDBIDs = "";
var strSelectedCompIDs = "";
var currentList=null;
var boolSearchMode = false;

function MF()
{
	return _swdoc.mainform;
}

//-- Paging Variables
var intPageNo = 1; // Page Number To Pass to Query
var strFilter = ""; // Search filter
var intPerPage = oSettings.get_setting("sysSqlPaging"); // defines the maximum amount of records to be displayed per page
if (_swdoc.webclient) _swdoc.intPerPage = 100; //overide for _swdoc.webclient

function setup_form_fields()
{
	//-- set field states
	_een(_swdoc.MF().btn_select, false);
	_een(_swdoc.MF().btn_subset, false);
	_een(_swdoc.MF().btn_remsub, false);
	_een(_swdoc.MF().btn_subclear, false);


	_een(_swdoc.MF().btn_delete, false);
	_een(_swdoc.MF().btn_view, false);
	_swdoc.currentList = _swdoc.MF().sl_results;

	//-- show hide buttons depending on searchmode
	_swdoc.boolSearchMode = (_swdoc.GetArg("searchmode")=="1");

	_evi(_swdoc.MF().btn_delete, (!_swdoc.boolSearchMode));
	_evi(_swdoc.MF().btn_view, (!_swdoc.boolSearchMode));
	_evi(_swdoc.MF().btn_select, (_swdoc.boolSearchMode));
	_evi(_swdoc.MF().btn_cancel, (_swdoc.boolSearchMode));	

	if(!_swdoc.boolSearchMode)_swdoc.SetTitle("Manage Organisation Records");


}

btn_search_OnPressed()
{
	//-- 19.01.2006 - NWJ
	//-- create search criteria
	_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;

	_een(btn_select, false);
	app.g.sqllist_clear(sl_results);

	//-- 07.12.2012 - nwj - use new create_search_params for use with stored query
	var boolAND = (lb_type.value==1);

	//-- Set Filter
	var strFilter = app.g.create_search_params(mainform,tb_table.text,"",boolAND,false);

	//--Update Document so Filter is Global
	_swdoc.strFilter = strFilter;

	//-- Add Paging
	strFilter += "&paged=true&start=";
	strFilter += intPageNo;
	strFilter += "&end=&webclient=";
	strFilter += _swdoc.webclient;

	//-- apply filter
	app.g.sl_filter(_swdoc.MF().sl_results,strFilter,null,0);

	_swdoc.MF().sl_results.Refresh();

	//Show Next Button
	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		_een(_swdoc.MF().btn_next,false);	
	}else
	{
		_een(_swdoc.MF().btn_next,true);	
	}
	_een(_swdoc.MF().btn_prev,false);
}

btn_subclear_OnPressed()
{
	//-- nwj - 2012-11-07 - pass no params which means clear result
	var strFilter = "";
	app.g.sl_filter(sl_sub,strFilter,null,0);
	sl_sub_OnItemSelected(0);

}

btn_remsub_OnPressed()
{
	app.g.sl_clearselected(sl_sub,null,0);

	//-- 07.12.2012 - nwj - create simple param to pass into storequery
	var strSubKeys= app.g.sl_get_valuesdel(sl_sub,0,",","");
	if(strSubKeys=="")
	{
		strSubKeys ="-1";	
	}

	var strFilter = "_kv=" + strSubKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);
	sl_sub_OnItemSelected(0);	

}

btn_subset_OnPressed()
{
	//-- 07.12.2012 - nwj - create simple param to pass into storequery
	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results	,tb_keycol.text,",","");
	var strSubKeys = app.g.sl_get_valuesdel(sl_sub,tb_keycol.text,",","");

	//-- if we already have sub calls
	if(strSubKeys!="")
	{
		strSelKeys += "," + strSubKeys;
	}

	var strFilter = "_kv=" + strSelKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);

}

btn_select_OnPressed()
{
	//-- get list of selected callrefs
	_swdoc.strSelectedKeys= app.g.sl_getselected_valuesdel(_swdoc.currentList,tb_keycol.text,",");
	_swdoc.strSelectedText = app.g.sl_getselected_textdel(_swdoc.currentList,tb_txtcol.text,",");
	_swdoc.strSelectedOther = app.g.sl_getselected_textdel(_swdoc.currentList,tb_othercol.text,",");	
	_swdoc.strSelectedCMDBIDs = app.g.sl_getselected_textdel(_swdoc.currentList,'fk_cmdb_id',",");	
	_swdoc.CloseForm();	
}

TabRes_OnTabSelected(nTab)
{
	if(nTab==0)
	{
		_swdoc.currentList = sl_results;
	}
	else
	{
		_swdoc.currentList = sl_sub;
		mainform.sl_sub.Refresh();
	}

	_een(btn_view, (app.g.sl_getselected_count(_swdoc.currentList)==1));
	_een(btn_delete, (_swdoc.currentList.rowCount()>0));			

}

btn_delete_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected company records?";
	app.g.sl_deldbrows(_swdoc.currentList,tb_keycol.value,null,strConfirm);

}

btn_next_OnPressed()
{
	_swdoc.intPageNo++;

	//-- Add Paging
	var strFilter = _swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&webclient="+_swdoc.webclient;

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		_een(_swdoc.MF().btn_next,false);	
	}
	_een(_swdoc.MF().btn_prev,true);
}

btn_prev_OnPressed()
{
	_swdoc.intPageNo--;

	//-- Add Paging
	var strFilter = _swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&webclient="+_swdoc.webclient;

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if (_swdoc.intPageNo == 1) 
	{
		_een(_swdoc.MF().btn_prev,false);
	}
	_een(_swdoc.MF().btn_next,true);

}
