//--
//-- based on the current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032
	
	//-- NWJ
	//-- assign _swdoc to cfg array (so we can refresh tasks list from task forms
	app.cfg.allocaterfcdoc(_swdoc.OC().callref,_swdoc)
		
	_swdoc.strOriginalSLA = _swdoc.opencall.priority;
	
	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}
	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;

	var strParams = "cr="+pfu(_swdoc.OC().callref)+"&sid="+_swdoc.oStage().pk_stage_id;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);

    var boolHasTasks = intTaskCount>0;
	var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(PG_BPM, BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));
	
	_een(_swdoc.MF().mbtn_mod_task,boolAddTasks);
	_swdoc.MF().TabDet.ShowTabItem(2,boolShowTasks);
	if(boolShowTasks)_swdoc.MF().TabDet_OnTabSelected(2);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}
	
	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);

	// DTH: Check if there are missing emails from the ABA Authorisers
	if (boolRequiresAuth)
	{
		var xmlmc = new XmlMethodCall;
		
		// Set up our input parameters
		xmlmc.SetValue("callref", _swdoc.OC().callref);
		xmlmc.SetValue("stage_id", _swdoc.OC().bpm_stage_id);
		
		// Invoke the method
		if(!xmlmc.Invoke("VPME", "checkAbaAuthEmailCount"))
		{
			MessageBox(xmlmc.GetLastError());
		}
		else
		{
			if (xmlmc.GetReturnValue("res1") > 0)
				MessageBox("There is no email address against one or more Affected Business Areas");
		}
	}
	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(PG_BPM, BPM_ADDAUTH,app.session.dataDictionary)) )
	if(_swdoc.oStage().flg_unanimous_auth==1)
	{
		_evi(_swdoc.MF().authWeighting,false);
	}	
	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(1, boolRequiresAuth||boolAddAuths);
	if(boolRequiresAuth||boolAddAuths)_swdoc.MF().TabDet_OnTabSelected(1);
	//if we require authorisation, or disable manual, do not enable status
	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	_een(_swdoc.MF().lb_wf_state,(!(boolTaskStop|| boolRequiresAuth)));
	
	_evi(_swdoc.MF().mbtn_mod_auth,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF
	
	_swdoc.MF().TabDet.ShowTabItem(4, false);	
	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(1, boolRequiresAuth||boolAddAuths);


	//-- if we have data form show button
	_een(_swdoc.MF().btn_bpm_form,((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));
	
	//-- select tab 0 or 1 depending on auth and if any authorisers are set
	_etab(_swdoc.MF().TabDet,(boolRequiresAuth)?1:0);
	
	_swdoc.setup_change_subtab();
	
	//-- whether we enable the progress button, only do so if there is a progress associated with the bpm process
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		var intCount = app.g.sqs_rowcount("call/common/cnt_progress_items","wf="+pfu(_swdoc.OC().bpm_workflow_id));
		if(intCount>0)
			_een(_swdoc.MF().btn_bpm_progress,true);
	}
			
	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);

	return true;
}

//-- given an analyst object (from app.global.js) load values for either monitored / raised by analyst
//-- for the rfc
function set_rfc_analyst(oAnalyst,boolMonitoredBy)
{
	if(!oAnalyst) return false;
	
	if(boolMonitoredBy)
	{
		_swdoc.RFC_EXT().monitored_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().monitored_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().monitored_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().monitored_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().monitored_by_email = oAnalyst.email;	
	}
	else
	{
		_swdoc.RFC_EXT().raised_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().raised_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().raised_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().raised_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().raised_by_email = oAnalyst.email;
	}
	_swdoc.UpdateFormFromData();
		
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_cis_to_change(strKeys, strCIText, strCIType)
{

	if(strKeys=="") return;
	//-- loop through selected CIs
	var strText = "";
	var strServiceText = "";
	var arrayCIkeys = strKeys.split(",");
	var arrayCItext = strCIText.split(",");	
	var arrayCItypes = strCIType.split(",");	

	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
        var ci_type_text = arrayCItypes[x];

		var itemType = "Configuration Item";
		
		//-- check that CI is not already in the list
		if(ci_type_text.indexOf("Service")>-1)
  		{
 			var itemType = "Service";
 			//-- check that CI is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,0);
			if (rowPos == -1)
			{ 
				//-- Relate the CI to the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'CPR-CAUSE'))
				{
					// RJC: F0101301 Adding Affected Business Areas from Service to the Call
					app.g.add_affected_bus_area_to_call(_swdoc.OC().callref, "CI", ci_item_text, "", false);
					if(strServiceText!="")strServiceText += ",";
					strServiceText += ci_item_text;
				}
			}
  		}else
  		{
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_configitems1,pk_ci_id,'pk_auto_id');
			if (rowPos == -1)
			{ 
				//-- Relate the CI to the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'CPR-CAUSE'))
				{
					// DTH: F0097653 Adding Affected Business Areas from CI to the Call
					app.g.add_affected_bus_area_to_call(_swdoc.OC().callref, "CI", ci_item_text, "", false);
					if(strText != "") strText +=",";
					strText += ci_item_text;
				}
			}
		}
		// -- SG, 02/07/2012, F0103324
		// Build array of CI's that have the Bespoke Terms field populated
		var rec = app.g.get_record("config_itemi", pk_ci_id);
		if(rec.recordresolvedmsg != '')
		{
    		bespokeTermsArray[countBespokeTerms] = new Array(2);
    		bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    		bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    		countBespokeTerms++;
		}
		
		// End of array building
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following configuration items, for change, were added to this change proposal.\n" + strText;
		var strActionSource = "Change Management";
		var strCode			= "Linking Configuration Items";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_configitems1.Refresh();
	}
	
	//-- apply call diary update for added CIs (if we have any to add)
	if (strServiceText!="")
	{
		strServiceText = "The following services, for change, were added to this change proposal.\n" + strServiceText;
		var strActionSource = "Change Management";
		var strCode			= "Linking Services";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strServiceText,strActionSource,strCode);
		_swdoc.EF().sl_services.Refresh();
	}
	
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_services_to_change(strKeys, strCIText)
{

	if(strKeys=="") return;
	//-- loop through selected CIs
	var strText = "";
	var strServiceText = "";
	var arrayCIkeys = strKeys.split(",");
	var arrayCItext = strCIText.split(",");	
	
	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Service";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	
		
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
		
		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,0);
		if (rowPos == -1)
		{ 
			// -- SG, 02/07/2012, F0103324
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building

			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'CPR-CAUSE'))
			{
				if(strServiceText!="")strServiceText += ",";
				strServiceText += ci_item_text;
				// RJC: F0101301 Adding Affected Business Areas from Service to the Call
				app.g.add_affected_bus_area_to_call(_swdoc.OC().callref, "CI", ci_item_text, "", false);
			}
		}
  	}   
	
	//-- apply call diary update for added CIs (if we have any to add)
	if (strServiceText!="")
	{
		strServiceText = "The following services, for change, were added to this change proposal.\n" + strServiceText;
		var strActionSource = "Change Management";
		var strCode			= "Linking Services";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strServiceText,strActionSource,strCode);
		_swdoc.EF().sl_services.Refresh();
	}
	// -- SG, 02/07/2012, F0103324
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, "Configuration Item");
}


//-- given a ci or list of cis link it to problem affect list
function link_affect_cis_to_change(strKeys,strCIText)
{
	if(strKeys=="") return;

	//-- loop through selected CIs
	var strText = "";
	var arrayCIkeys = strKeys.split(",");
	var arrayCItext = strCIText.split(",");	
	
	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
		
		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_impactcis1,pk_ci_id,'pk_auto_id');
		if (rowPos == -1)
		{ 
			
			// -- SG, 02/07/2012, F0103324
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building
			
			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'CPR-AFFECT'))
			{
				if(strText!="")strText += ",";
				strText += ci_item_text;
			}
		}
	}   
	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following configuration items have been identified as being impacted by this change proposal.\n" + strText;
		var strActionSource = "Change Management";
		var strCode			= "Linking Configuration Items";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_impactcis1.Refresh();	
		_swdoc.EF().sl_impactcis1_OnItemSelected(0);
	}
	// -- SG, 02/07/2012, F0103324
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function link_incidents_to_change(strSelectedIncs)
{
	var array_incs = strSelectedIncs.split(",");  
	var strText = "This change proposal has been associated with the following incidents\n\n";
	var boolINCs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_incs.length;x++)
    {
        var inc_callref = array_incs[x];
        var inc_callref_text = app.g.pad_callref(inc_callref);
		
		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_incs1,inc_callref,0);
		if (rowPos == -1)
		{ 		
			boolINCs=true;
			//-- call g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'CPR-INCIDENT'))
			{
				strText += "Incident (" + inc_callref_text + ") associated\n";
				
				//-- update known error diary
				strIncText = "The change proposal (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this incident.";
				app.g.new_diaryevent(inc_callref,strIncText,"Change Management","Change Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolINCs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Incident Association");
		_swdoc.EF().sl_incs1.Refresh();
		
		//-- Now check if we should link new ci items as being affect
		if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyIncidentCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var oRes = app.cmdb.get_call_ci_info(strSelectedIncs , "INCIDENT", "");
			if(oRes.keys!="")_swdoc.link_affect_cis_to_change(oRes.keys,oRes.text);
		}		
	}
}

function link_cause_incidents_to_change(strSelectedIncs)
{
	var array_incs = strSelectedIncs.split(",");  
	var strText = "This change proposal has been associated as the cause of the following incidents\n\n";
	var boolINCs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_incs.length;x++)
    {
        var inc_callref = array_incs[x];
        var inc_callref_text = app.g.pad_callref(inc_callref);
		
		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_causeinc,inc_callref,0);
		if (rowPos == -1)
		{ 		
			boolINCs=true;
			//-- call g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'CPR-CINCIDENT'))
			{
				strText += "Incident (" + inc_callref_text + ") associated\n";
				
				//-- update known error diary
				strIncText = "The Change Proposal (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated as a cause of this incident.";
				app.g.new_diaryevent(inc_callref,strIncText,"Change Management","Change Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolINCs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Incident Association");
		_swdoc.EF().sl_causeinc.Refresh();
		
		//-- Now check if we should link new ci items as being affect
		if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyIncidentCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var oRes = app.cmdb.get_call_ci_info(strSelectedIncs , "INCIDENT", "");
			if(oRes.keys!="")_swdoc.link_affect_cis_to_change(oRes.keys,oRes.text);
		}		
	}
}

function link_cause_problems_to_change(strSelectedProbs)
{
	var array_probs = strSelectedProbs.split(",");  
	var strText = "This change proposal has been associated as the cause of the following problems\n\n";
	var boolPROBs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_probs.length;x++)
    {
        var prob_callref = array_probs[x];
        var prob_callref_text = app.g.pad_callref(prob_callref);
		
		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_causeprob,prob_callref,0);
		if (rowPos == -1)
		{ 		
			boolPROBs=true;
			//-- call g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,prob_callref,'CPR-CPROBLEM'))
			{
				strText += "Problem (" + prob_callref_text + ") associated\n";
				
				//-- update known error diary
				strProbText = "The Change Proposal (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated as a cause of this problem.";
				app.g.new_diaryevent(prob_callref,strProbText,"Change Management","Change Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolPROBs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Problem Association");
		_swdoc.EF().sl_causeprob.Refresh();
	}
}

function remove_change_incidents(strCallRefs,boolFromKnownErrorRemoval)
{
	//-- if these incidents are being removed because we removed known errors
	if(boolFromKnownErrorRemoval)
	{
		//-- check if the ke incidents exists in this problem
		var arrayINCS = strCallRefs.split(",");
		for(var x=0;x<arrayINCS.length;x++)
		{
			if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_incs1,arrayINCS[x],0)!=-1)	
			{
				//-- at least ke one incident belongs to this change
				//-- if not confirm exit
				if(!confirm("There are associated incident records, that are linked to the known errors you are removing. Do you want to remove these records as well?"))return;			
				
				//-- if there is at least one break.
				break;	
			}
		}
	}

	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'CPR-INCIDENT'))
	{
		var arrCallrefs = strCallRefs.split(',');
		var strText = "This change proposal has been dis-associated from the following Incidents\n\n";
		
		for(var x=0;x<arrCallrefs.length;x++)
		{
			strText += "Incident (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
			
			var strRFCText = "The change proposal (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this Incident";
			app.g.new_diaryevent(arrCallrefs[x], strRFCText,"Change Management","Change Dis-association");
		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Incident Dis-association");
	}

	_swdoc.EF().sl_incs1.Refresh();	
	_swdoc.EF().sl_incs1_OnItemSelected(0);		
	_swdoc.remove_selected_incident_cis(strCallRefs);			
}

function remove_cause_incidents(strCallRefs)
{
	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'CPR-CINCIDENT'))
	{
		var arrCallrefs = strCallRefs.split(',');
		var strText = "This change proposal has been dis-associated as the cause of the following Incidents\n\n";
		
