			var strActionSource = "Change Management";
			var strCode			= "Remove Services";
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
			
			// RJC: F0101301 Remove Affected Business Areas from the call. These removed ABAs were linked to the Services which have been removed
			app.g.remove_affected_bus_area_from_call(_swdoc.OC().callref, "", strCIs, "CI");			
    	}			

	    //-- re-filter list	
		_swdoc.EF().sl_services.Refresh();
		_swdoc.EF().sl_configitems1.SetRowSelected(0);
		_swdoc.EF().sl_configitems1_OnItemSelected(0);
		
    }
