function filter_impact_customers(oForm)
{
	//-- get list of customers impacted by these incidents and refresh cust impact list
	oForm.sl_incs1.Refresh();
	var strCustFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'cust_id',",","'",true);
	
	//-- get customers that use affected cis
	_swdoc.EF().sl_impactcis1.Refresh();
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis1,'pk_auto_id',",");	
	var strCustIidsFromImpactCIs = app.cmdb.get_ci_customer_ids(strImpactedCIs,true);
	if(strCustIidsFromImpactCIs!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactCIs;	
	}

	//-- get customers affected by the associated kes
	oForm.sl_kes1.Refresh();
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strCustIidsFromImpactKes = app.itsm.get_problem_impact_customerids(strKeCallrefs,true);
	if(strCustIidsFromImpactKes!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactKes;	
	}
	if(_swdoc.oCust().keysearch!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=_swdoc.oCust().keysearch;	
	}

	if(strCustFilter=="")strCustFilter="-1";
	_slf(oForm.sl_customerimpact1,"_kv=" + strCustFilter);
	oForm.sl_customerimpact1.Refresh();			
}

function filter_impact_orgs(oForm)
{
	//-- get list of orgs impacted by impacted customer
	oForm.sl_incs1.Refresh();
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'fk_company_id',",","'",true);
	
	//-- get orgs that use affected cis
	_swdoc.EF().sl_impactcis1.Refresh();
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- 2.4 include orgs that use change ci
	//-- get orgs that use change cis
	_swdoc.EF().sl_configitems1.Refresh();
	var strChangeCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");
	_swdoc.EF().sl_services.Refresh();
	var strChangeServices = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,'pk_auto_id',",");
	if(strChangeServices!="")
	{
		if(strChangeCIs!="")strChangeCIs+=",";
		strChangeCIs+=strChangeServices;
		
	}
		
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strChangeCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- get orgs affected by the associated kes
	oForm.sl_kes1.Refresh();
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs!="")
	{
		var strOrgIdsFromImpactKes = app.itsm.get_problem_impact_orgids(strKeCallrefs,true);
		if(strOrgIdsFromImpactKes!="")
		{
			if(strOrgFilter!="")strOrgFilter+=",";
			strOrgFilter+=strOrgIdsFromImpactKes;
		}
	}
	if(_swdoc.oCompany().pk_company_id!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=_swdoc.oCompany().pk_company_id;
	}
	
	if(strOrgFilter=="")strOrgFilter="-1";
	_slf(oForm.sl_orgimpact,"_kv=" + strOrgFilter);
	oForm.sl_orgimpact.Refresh();	
}

function get_company_cis()
{
	var strKeyValues = app.cfg.get_company_cikeys(_swdoc.oCompany().pk_company_id,true,true);	

	var strParams = "fk_company_id="+pfu(strCompID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
		strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	_swdoc.companyCIIDS = strKeyValues;

}

function check_cis()
{
	if(_swdoc.EF().sl_configitems1.rowCount() =="")
		return;
	var boolCIRemoved = false;
	if(_swdoc.companyCIIDS == "")
		_swdoc.get_company_cis();
	var arrCIS = _swdoc.companyCIIDS.split(",");
	for (var x=_swdoc.EF().sl_configitems1.rowCount();x > -1;x--)
	{
		var strValue = _swdoc.EF().sl_configitems1.GetItemText(x,1);
		if(strValue !=""){
			if(in_array(arrCIS,strValue))
			{
				_swdoc.EF().sl_configitems1.SetRowSelected(x,false,true);
			}
			else
			{
				_swdoc.EF().sl_configitems1.SetRowSelected(x,true,true);
				boolCIRemoved = true;
			}
		}
	}
	_swdoc.EF().mbtn_cci_actions1_OnMenuItem('test',1);
	if(boolCIRemoved)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");
}

function search_for_cis(funcCallback)
{
	// F0105843 Start
	//if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;	// This is replaced by the new function, which includes the Can Edit check
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "SearchForCI", true)) return;
	// F0105843 End

    //-- NWJ 08.03.2004
    //-- open CI search form in select mode
    var strURL = "canreset=1&fk_company_id=" + pfu(_swdoc.oCust().fk_company_id);    
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="")
		{
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
		else
		{
			_swdoc.link_cause_cis_to_change(sResults.selectedkeys,sResults.selectedtext,sResults.selectedtypes);
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
	});
		
}

function search_for_services(funcCallback)
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "SearchForService", true))
	{
		if ((funcCallback != undefined) && (funcCallback != null))
		{
			funcCallback();
		}
	}
	else
	{
		// F0105843 End

    	//-- JPG 22.01.2015
    	//-- open CI search form in select mode
    	//Definition mode for the new window
    	var strURL = "searchmode=1&multiselect=1&canreset=1&fk_company_id=" + pfu(_swdoc.oCust().fk_company_id);
    	//Path of the new window. Search for services better filters
		app.g.popup("manage_portfolio", strURL,function(sResults)
		{
			if (sResults)
			{
				if (sResults.document.strSelectedKeys=="")
				{
					if ((funcCallback != undefined) && (funcCallback != null))
					{
						funcCallback();
					}
				}
				else
				{ 
					_swdoc.link_cause_services_to_change(sResults.document.strSelectedKeys,sResults.document.strSelectedText);
					if ((funcCallback != undefined) && (funcCallback != null))
					{
						funcCallback();
					}
				}
			}
			else
			{
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
		});
	}
}

function check_act_warnings()
{
	var oActs = new Array();
	for(var i = 0; i<_swdoc.MF().sl_activity.rowCount(); i++)
	{
		var oSched = new Object();
		oSched.pk_auto_id = _swdoc.MF().sl_activity.GetItemTextRaw(i, 'pk_auto_id');
		oSched.startDatex = app.g.fix_epoch(_swdoc.MF().sl_activity.GetItemTextRaw(i, 'startx'));
		oSched.endDatex = app.g.fix_epoch(_swdoc.MF().sl_activity.GetItemTextRaw(i, 'endx'));
		oSched.title = _swdoc.MF().sl_activity.GetItemTextRaw(i, 'title');
		oActs.push(oSched);
	}

	var strCIids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
	var strServiceids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
	if(strCIids!="" && strServiceids!="")
	{
		strCIids+=",";
		strCIids+=strServiceids;
	}

	if(oActs.length>0)
	{
		_swdoc.strWarning = app.cmdb.check_activity_schedule(strCIids, oActs, true, _swdoc.OC().itsm_requiredbyx, _swdoc.OC().callref);
		if(_swdoc.strWarning!="")
			_evi(_swdoc.MF().txt_warning,true);
		else
			_evi(_swdoc.MF().txt_warning,false);
	}
}

function remove_kbdoc(strDOCs)
{
	if(app.g.unrelate_kbdoc(_swdoc.OC().callref,strCallRefs,'Modify'))
	{
		var arrDOCs = strDOCs.split(',');
		var strText = "This change proposal has been dis-associated with the following KB Documents\n\n";
		
		for(var x=0;x<arrDOCs.length;x++)
		{
			strText += "Knowledgebase Document (" + arrDOCs[x] + ") dis-associated\n";
		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Knowledgebase Document Dis-association");
	}
	_swdoc.EF().sl_kbs.Refresh();
}


function setup_change_subtab()
{
	var boolShowBreach = ((app.g.fix_epoch(_swdoc.OC().fixbyx) < app.global.GetCurrentEpocTime() )&& (!(_swdoc.OC().slafix>0)) || (_swdoc.OC().slafix>0 && _swdoc.OC().withinfix==0));
	_swdoc.MF().TabDesc.ShowTabItem(4,boolShowBreach);
	var boolShowRes = (_swdoc.OC().resolve_text!="");
	_swdoc.MF().TabDesc.ShowTabItem(3,boolShowRes);
	var boolShowClose = (_swdoc.OC().close_text!="" && _swdoc.OC().status>14);
	_swdoc.MF().TabDesc.ShowTabItem(5,boolShowClose);
	
	if (!boolShowBreach && !boolShowRes && !boolShowClose)
	{
		_etab(_swdoc.MF().TabDesc,0);
	}
	else
	{
		if(boolShowRes)
		{
			_etab(_swdoc.MF().TabDesc,3);
		}
		if(boolShowClose)
		{
			_etab(_swdoc.MF().TabDesc,5);
		}
		if (boolShowBreach)
		{
			// get list of support groups logged in analyst is a member of
			var strAnalystGroups = app.g.get_all_analyst_groups(app.session.analystId);
			var arrAnalystGroups = strAnalystGroups.split(",");
			var index;
			var boolInGroup=false;
			// loop through list of support groups
			for (index in arrAnalystGroups){
				// check if the support group this call is assigned to is in list of groups
				if(arrAnalystGroups[index]==_swdoc.OC().suppgroup){
					boolInGroup=true;
					break;
				}
			}
			/* if logged in analyst is the owner of this call or is the member of
		   	the support group this call is assigned to, make the Breach Text field mandatory */
			if(_swdoc.OC().owner==app.session.analystId || boolInGroup) {
				_ema(_swdoc.MF().tb_breachtext1,true);
			}
			_etab(_swdoc.MF().TabDesc,4);
		}
	}
	_swdoc.load_diary_textfields();
}


function load_diary_textfields(boolLastOnly)
{
	//-- load full diary text into form fields - bug fix 62811
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_last_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_ongoing1);
	if((boolLastOnly==undefined)||(!boolLastOnly))app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext1);
}

function process_impact_change(strImpact)
{
	if(_swdoc.OC().itsm_sladef!="")
	{
		var strSLA = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, strImpact, _swdoc.OC().itsm_urgency_level);
	}
	else
		var strSLA = app.itsm.get_sla_frommatrix(strImpact, _swdoc.OC().itsm_urgency_level);
	
	if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
}

// F0105843 START
// Creating a function to Change the Customer record. This can be called from the Show Me menu and from the Action menu.
// This approach means that we can control access to this action
function search_for_customer(funcCallback)
{
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "ChangeCustomer", true))
	{
		if ((funcCallback != undefined) && (funcCallback != null))
		{
			funcCallback();
		}
	}
	else
	{
	
   		var strURL = "autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCompany().pk_company_id;
		app.g.search_for("customers",false,strURL,function(sResults)
		{
	    	if(sResults.selectedkeys=="")
	    	{
	    		if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
	    	}
	    	else
	    	{
    			var arrKeySearch = sResults.selectedkeys.split(",");
    			
    			// F0110887 START
    			var arrCompanyId = sResults.selectedcompanyids.split(",");
    			
    			if(arrKeySearch[0]!=_swdoc.oCust().keysearch)
    			{
    				//-- Get Company Info
      				var strParams = "table=userdb&_swc_keysearch=" + pfu(arrKeySearch[0]);
					var oRS = app.g.get_sqrecordset("select.tablebycol",strParams);
					if(oRS.Fetch())
					{
						var strOrgID = oRS.GetValueAsString('fk_company_id');
					}
      				var oRS = app.g.get_sqrecordset("company/get_expiry_date","company_id="+strOrgID);
  					if (oRS.Fetch())
  					{ 
      					var intExpiryEpoch = app.g.fix_epoch( app.g.get_field(oRS,"SUPPORTEXPIRYX"));
					}
					var boolOrgSupported = new Boolean(app.g.checkOrgSupport(strOrgID, intExpiryEpoch, true));
					if (boolOrgSupported==true)
					{
    					_swdoc.ChangeRecord("userdb", arrKeySearch[0]);
					}
				}
				// F0110887 END
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
		
		});
	}

}
// F0105843 END

						OnDataChanged()
{
	if(_swdoc.intLastOCStatus != _swdoc.OC().status)
	{
		_swdoc.intLastOCStatus = _swdoc.OC().status;
		if(_swdoc.OC().status>15)
		{
			mainform.SetTimer(5005, 100);				
		}
		if(_swdoc.OC().status==6)
		{
			mainform.SetTimer(5005, 100);				
		}
	}
	//-- status has changed
	if(_swdoc.strLastBPM_Status != _swdoc.OC().bpm_status_id)
	{
		_swdoc.strLastBPM_Status = _swdoc.OC().bpm_status_id;			
		//-- have to set time because form does not refresh properly
		if(IsObjectDefined('mainform'))
		{
			mainform.SetTimer(5005, 100);				
		}
	}
	if(_swdoc.OC().bpm_execvpme==1)
	{
		_swdoc.MF().sl_wfauth.Refresh();
	}
	
	//TK This is Slowing Form Down
	//-- F0088933
	if(_swdoc.OC().last_text != _swdoc.MF().tb_ongoing1.value)
	{
		app.g.get_call_last_updatetxt(_swdoc.OC().callref,true,_swdoc.MF().tb_ongoing1);
	}
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			_een(_swdoc.MF().btn_actionsts, true);
	}
	
}

						OnSaveData()
{
	//--TK Performance Monitoring
	_swdoc.startTime = new Date().getTime();
	
	//-- status has changed
	if(_swdoc.strLastBPM_Status != _swdoc.OC().bpm_status_id)
	{
		if(bpm_stage.flg_requireci==1)
		{
			if(_swdoc.EF().sl_configitems1.rowCount() < 1)
			{
				if(_swdoc.EF().sl_services.rowCount() < 1)
				{
					MessageBox("Before changing the BPM Status for this request you have to associate at least one affected configuration item or service");
					return false;
				}
			}
		}
	}

	// DTH: Check that the Desired End Date is after the Desired Start Date
	var intRequestedDate = app.g.fix_epoch(_swdoc.OC().requested_start_datex);
	var intRequestedEnd = app.g.fix_epoch(_swdoc.OC().requested_end_datex);	
	if ((intRequestedDate > 0) && (intRequestedEnd > 0) && (intRequestedEnd < intRequestedDate))
	{
		MessageBox("The Desired End Date should occur after the Desired Start Date");
		return false;
	}
	return true;
}

						OnChangeCallProfile()
{
	// F0105843 Start
	//return (app.itsm.can_edit(_swdoc.OC().callclass,true));	// This is replaced by the new function, which includes the Can Edit check
	return (app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "ChangeCallProfile", true));
	// F0105843 End

	return true;
}

						OnRecordChanged(strTable)
{
	if(strTable=="userdb")
	{
		//-- check customer status message
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		
		if (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")!=1){
		//###20120801 IF not supporting external companies, then skip setting new company
			//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
			if(_swdoc.oCust().fk_company_id != "" && _swdoc.oCust().fk_company_id != _swdoc.oCompany().pk_company_id)
			{
				//-- rjc - 2.4 - resolve company record if company doesnt exist
				_swdoc.ChangeRecord('company',_swdoc.oCust().fk_company_id);
				if(_swdoc.oCompany())
				{
					//-- apply diary
 					var strText = "The related record [Company] was changed for this call. Old Value: '"+_swdoc.strOrigCompany +"', New Value: '"+_swdoc.oCompany().companyname+"'";
 					_swdoc.strOrigCompany =_swdoc.oCompany().companyname;
					app.g.new_dairyevent(_swdoc.OC().callref,strText,"","");	
	
					_swdoc.boolResolvingCust = false;
	
					if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
				}
				else
				{
					//-- there is adata issue here as the customers organisation does not exist
					MessageBox("The organisation that this customer belongs to cannot be found on the system. Please contact your Supportworks administrator.");
				}
			}
		}
		else
		{
			//### allow for message to show
			_swdoc.boolResolvingCust = false;
			if(_swdoc.oCust().fk_company_id != "" && _swdoc.oCust().fk_company_id != _swdoc.OC().fk_company_id)
			{
				var strTempComp = app.g.get_record("company", _swdoc.oCust().fk_company_id);
				if(strTempComp.recordresolvedmsg!="")MessageBox(strTempComp.recordresolvedmsg);
			}

		}
	}
	
	if(strTable=="company")
	{
		//-- apply diary
 		var strText = 'The related record [Company] was changed for this call. Old Value: "'+_swdoc.strOrigCompany +'", New Value: "'+_swdoc.oCompany().companyname+'"';
 		_swdoc.strOrigCompany =_swdoc.oCompany().companyname;
		app.g.new_dairyevent(_swdoc.OC().callref,strText,"","");
		
		var boolExternalCompany = (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1);
		
		var funcFinalProcessing = function()
		{
			//###20120720 - ensure that site etc gets empties on new company selection.
			if( _swdoc.boolEmptySites )
			{
				_swdoc.OC().costcenter = "";
				_swdoc.OC().fk_dept_code = "";
				_swdoc.OC().fk_contract_id = "";
				_swdoc.MF().lb_contract.value = "";
				_swdoc.MF().lb_contract.text = "";
				_swdoc.MF().fld_oc_dept.value = "";
				_swdoc.MF().fld_oc_dept.text = "";
				_swdoc.OC().site = "";
				_swdoc.Save();
			}
		
			app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract);	
			
			//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
			if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
			
 			_swdoc.check_cis();	
			_swdoc.UpdateFormFromData();
		}

 		if(dd.GetGlobalParamAsNumber("Application Settings/Change/OnOrganisationResolveCustomer")==1)
		{	
			var strURL = "autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCompany().pk_company_id;
			app.g.search_for("customers",false,strURL,function(sResults)
			{
				if(sResults.selectedkeys!="")
    			{ 
    				var arrKey = sResults.selectedkeys.split(",");
 					if (!boolExternalCompany) _swdoc.ChangeRecord("userdb",  arrKey[0]);
 				}
 				else
 				{
 					//###20120718 When supporting external companies, then IF No customer selected (i.e. cancelled), then Old Customer should stay and be changed by using Customer Search
 			 		if (!boolExternalCompany) _swdoc.ResetRecord('userdb');
 				}
 				funcFinalProcessing();
			});
		}
		else
		{	
 			 if (!boolExternalCompany) _swdoc.ResetRecord('userdb');
 			 funcFinalProcessing();
		}
	}
}

						OnFormClosing()
{

	if(IsObjectDefined("mainform"))
	{	
		//-- NWJ
		//-- remove from openrfcs array
		app.cfg.deallocaterfcdoc(_swdoc.OC().callref)
	}
   app.g.remove_me_document("REQUEST", _swdoc.OC().callref);
	return true;
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	// TODO: Add your event handler code here
      var strBinding = strTable + "." + strColumn;
      //-- check if requires auth has changed

      switch(strBinding)
      {
 /*         case "opencall.itsm_impact_level":
              var strSLA = app.itsm.get_sla_frommatrix(strValue, _swdoc.OC().itsm_urgency_level);
              if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
              break;
          case "opencall.itsm_urgency_level":                   
              var strSLA = app.itsm.get_sla_frommatrix(_swdoc.OC().itsm_impact_level,strValue);
              if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
              break;       */
              
			case "opencall.itsm_impact_level":
				_swdoc.process_impact_change(strValue);
				break;
				
			case "opencall.itsm_urgency_level":                   
				if(_swdoc.OC().itsm_sladef!="")
				{
					var strSLA = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, strValue);
				}else
					var strSLA = app.itsm.get_sla_frommatrix(_swdoc.OC().itsm_impact_level, strValue);
				if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
				break;                           
      }
}

						OnDataSaved()
{
	if(_swdoc.strOriginalSLA != _swdoc.strMatrixSLA)
    {
	    app.itsm.handle_sla_matrix_change(_swdoc);
	}

	//-- F0105032
	for(strBinding in _swdoc.arrAudit)
	{
		//--TK Moved into For Loop so only run when needed
		var intEpochTime = app.global.GetCurrentEpocTime();
		var oAnalyst = app.g.get_sys_record("swanalysts",app.session.analystId);
		var oGroup = app.g.get_sys_record("swgroups",app.session.groupId);
		
		var arrBinding = strBinding.split(".");
		var strTable = arrBinding[0];
		var strColName = arrBinding[1];
		var oldValue = _swdoc.arrAudit[strBinding];
		var newValue = _swdoc[strTable][strColName];
		if(app.g.dd_isnumeric(strTable, strColName))
		{
			if(newValue<0)
				newValue = app.g.fix_epoch(newValue);
			if(oldValue<0)
				oldValue = app.g.fix_epoch(oldValue);
		}
		if(oldValue!=newValue)
		{
			var strFieldDisplay = dd.tables[LC(arrBinding[0])].columns[arrBinding[1]].DisplayName;
			var strFmtOldValue = app.g.dd_format(arrBinding[0], arrBinding[1], oldValue);
			var strFmtNewValue = app.g.dd_format(arrBinding[0], arrBinding[1], newValue);
			var oInsertTmp = new oXmlmcInsert('oc_audit');
			oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
			oInsertTmp.setfield('updatetimex',intEpochTime);
			oInsertTmp.setfield('updatedby',app.session.analystId);
			oInsertTmp.setfield('updatedbyname',oAnalyst.name);
			oInsertTmp.setfield('updatedbygroup',app.session.groupId);
			oInsertTmp.setfield('updatedbygroupname',oGroup.name);
			oInsertTmp.setfield('databinding',strBinding);
			oInsertTmp.setfield('bindingfield',arrBinding[1]);
			oInsertTmp.setfield('bindingtable',arrBinding[0]);
			oInsertTmp.setfield('fielddisplay',strFieldDisplay);
			oInsertTmp.setfield('oldvalue',oldValue);
			oInsertTmp.setfield('newvalue',newValue);
			oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
			oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
			oInsertTmp.execute();
			_swdoc.arrAudit[strBinding] = newValue;
		}
	}	//-- EOF F0105032

	
	// Create the ILA Call History record if the selected ILA Options have changed
	if (_swdoc.strILAOptions != _swdoc.strOrigILAOptions)
	{
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("analystid", app.session.analystId);
		xmlmc.SetValue("callref", _swdoc.OC().callref);
		xmlmc.SetValue("weighting", _swdoc.OC().ila_weighting);
		xmlmc.SetValue("impact_level", _swdoc.OC().itsm_impact_level);
		xmlmc.SetValue("usage_area", "Change Log Call Form");
		xmlmc.SetValue("ila_options", _swdoc.strILAOptions);
		/*
		if (app.g.ila_changedetails_diary_on_ila_change()) // DTH: Get the setting to determine if a Diary Update should be made
		{
			xmlmc.SetValue("create_diary", 1); // Creating a Diary entry when ILA changed on Call Details Form
		}
		else
		{
			xmlmc.SetValue("create_diary", 0); // No Diary entry requested based on settings
		}
		*/
		xmlmc.SetValue("create_diary", (app.g.ila_changedetails_diary_on_ila_change())?1:0); // Create a Diary Entry if the corresponding setting is true
		
		xmlmc.SetValue("prev_impact_level", _swdoc.strOrigImpact);
		xmlmc.SetValue("prev_weighting", _swdoc.strOrigWeighting);
		
		// Invoke the method
		if(!xmlmc.Invoke("VPME", "ilaCreateCallHist"))
		{
			MessageBox(xmlmc.GetLastError());
		}
	}

	//--TK Performance 
	if(oSettings.get_setting("qatime")==1)
	{
		_swdoc.endTime = new Date().getTime();
		var intTime = _swdoc.endTime - _swdoc.startTime;
		app.g.qa_log_time("Change Details Saved", intTime);
	}

}

						OnFormLoading(strFormName, strCallclass)
{
	//--TK Performance Monitoring
	_swdoc.startTime = new Date().getTime();
	
	// TODO: Add your event handler code here
	if(app.itsm.can_view(strCallclass,true)==false) return false;


	return true;
}

						OnFormLoaded(strFormName, strCallclass)
{
	// TODO: Add your event handler code here
	//--TK Disable Buttons if Call Closed
	if(_swdoc.OC().status>14)
	{
		_een(_swdoc.MF().mbtn_add_activity,false);
		_een(_swdoc.MF().btn_add_risk1,false);
		_een(_swdoc.MF().btn_add_aba,false);
		_een(_swdoc.MF().btn_add_backout,false);
	}
	//--TK Performance 
	if(oSettings.get_setting("qatime")==1)
	{
		_swdoc.endTime = new Date().getTime();
		var intTime = _swdoc.endTime - _swdoc.startTime;
		app.g.qa_log_time("Change Details Loaded", intTime);
	}
	
	//--F0110887 Start
	if(_swdoc.oCust().fk_company_id != "")
	{
		var oRS = app.g.get_sqrecordset("company/get_expiry_date","company_id="+_swdoc.oCust().fk_company_id);
  		if (oRS.Fetch())
  		{ 
			var intExpiryEpoch = app.g.fix_epoch(app.g.get_field(oRS,"supportexpiryx"));
			app.g.checkOrgSupport(_swdoc.oCust().fk_company_id, intExpiryEpoch, true);
		}
	}
	//--F0110887 End
	
}

