						btn_pick_sb_OnPressed()
{
    //-- NWJ 18.03.2004
   //-- open analyst search form in select mode

    // Create an instance of a PopupForm class
    var form = new PopupForm();

    // Open the form TbSites
    if(form.Open("tbAnalysts","",""))
    {
        //-- Access the forms _swdoc and call the _swdoc.GetVariable() method with the name of the variable
        var stranalystid = form.document.analystid;
        if (stranalystid == "") return;

        //-- assign values
        var oe = elements;
        _ete(oe["db.rfc_sb_id"] , stranalystid);
        _ete(oe["db.rfc_sb_name"] , form.document.name);
        _ete(oe["db.rfc_sb_sg"] , form.document.group);
        _ete(oe["db.rfc_sb_tel"] ,form.document.phone);
        _ete(oe["db.rfc_sb_email"] , form.document.email);
                _swdoc.opencall.itsm_rfc_sb_id = stranalystid;
                _swdoc.opencall.itsm_rfc_sb_name = form.document.name;
                _swdoc.opencall.itsm_rfc_sb_group = form.document.group;
                _swdoc.opencall.itsm_rfc_sb_tel = form.document.phone;
                _swdoc.opencall.itsm_rfc_sb_email = form.document.email;
    }
    
}

						btn_workdone_OnPressed()
{

    if ((_swdoc.opencall.status == 3) ||(_swdoc.opencall.status==2))
    {
            MessageBox("This RFC has not been accepted - Please accept the RFC as being under your control");
            _swdoc.AcceptCall();    
            return;
    }
    //-- save rfc after work completed
    //-- check that user has entered required details
    if (tb_rfc_workstatus.text == 0)
    {
        MessageBox("You have to enter 'RFC - Work Done' details in order to save the RFC",MB_ICONEXCLAMATION);
        return false;
    }

     //-- ok to save
    _swdoc.Save();

    var myobject = new Object();
    myobject.task = "logrfcevent";
    myobject.currentstep = _swdoc.GetVariable("rfc_step");
    myobject.nextstep = _swdoc.opencall.itsm_rfc_step;

    if (myobject.currentstep != myobject.nextstep)
    {

            extform.jscript_OnLeftClick(myobject);
            _swdoc.SetVariable("datasaved","true");
            mainform.OnFormLoaded();
    }

}

