						OnFormLoaded()
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		var strParams = "fk_workflow_id="+pfu(bpm_workflow.pk_workflow_id);
   		var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
		while(oRS.Fetch())
		{
			var strBinding = app.g.get_field(oRS,"fieldname");
			arrBinding = strBinding.split(".");
			_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
		}
	}//-- EOF F0105032


 	//-- setup form based on process stage
	if(_swdoc.setup_process_fields(false))
	{
		//-- load associated cause CIs
		_swdoc.EF().sl_configitems1.Refresh();
	}
	if(app.g.remote_support_enabled())
	{
		_evi(_swdoc.MF().btn_remotesupport, true);	
	}
	
	//-- if edit only disable status field for workflow
	// F0105843 Start
	//if (!app.itsm.can_edit(_swdoc.OC().callclass,false))	// This is replaced by the new function, which includes the Can Edit check
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "ChangeBpmStage", false))
	// F0105843 End	
	{
		_een(lb_wf_state,false);
	}
	
	// F0105843 Start
	// Display a label to indicate that alterations to the data on the form will not be saved based on the edit control level
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "SaveForm", false))	
	{
		_evi(lbl_cant_edit,true);
	}
	// F0105843 End
	
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	var bSupportExternalCompanies = ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1));
	_een(_swdoc.MF().lb_company,bSupportExternalCompanies);
	if(!bSupportExternalCompanies)
	{
		//-- check if we should hide
		_evi(_swdoc.MF().lb_company,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));
		//alter filter if we do not support external
		_slf(_swdoc.MF().lb_oc_cc,"");
		_slf(_swdoc.MF().lb_oc_site1,"");
		_slf(_swdoc.MF().fld_oc_dept,"");
	}
	_evi(_swdoc.MF().lbl_company,_swdoc.MF().lb_company.visible);
	//-- eof hide / enable	  
	
	_swdoc.strOrigCompany = _swdoc.oCompany().companyname; 
	_swdoc.intLastOCStatus = _swdoc.OC().status;
	
	_swdoc.check_act_warnings();	
	_ete(_swdoc.MF().lbl_oc_callref,app.g.callref_pad(_swdoc.OC().callref));
	
	//-- Show or hide contract field based on whether we support external companies
	_evi(_swdoc.MF().lbl_contract, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	_evi(_swdoc.MF().lb_contract, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	//-- Filter Contract List for Selected company
	app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract, _swdoc.OC().itsm_sladef);
	_swdoc.strLastBPM_Status = _swdoc.OC().bpm_status_id;
	
	// DTH: display the Impact Review button if Impact Level Assessment was enabled for the Log Change Request form
	_swdoc.boolILAReviewEnabled = app.g.ila_changelogcall_enabled(); // Impact Level Assessment Enabled
	_een(_swdoc.MF().btn_ila_review, _swdoc.boolILAReviewEnabled); // enable the Assessment button based on setting
	
	// DTH: Generate a list of the IDs for the selected Impact Level Assessment Options
	var strParams = "cr="+_swdoc.OC().callref;
	var oRS = app.g.get_sqrecordset("call/change/get_ila_options",strParams);
	while(oRS.Fetch())
	{
		if (_swdoc.strILAOptions != "") _swdoc.strILAOptions += ",";
		_swdoc.strILAOptions += app.g.pfs(app.g.get_field(oRS,"FK_ILA_OPTION_ID"));
	}
	//-- F0100940
	_swdoc.strOrigILAOptions = _swdoc.strILAOptions;
	
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			_een(_swdoc.MF().btn_actionsts, true);
	}
	//--F0108045
	app.g.add_me_document("REQUEST", _swdoc.OC().callref, _swdoc);

}

						btn_bpm_form_OnPressed()
{
	//-- open stage or workflow  data form
	
	//-- get the data table
	var strUseTable  = _swdoc.oWF().ext_db_table
	
	
	//-- first make sure there there is a callref rel in this table if not make one
	if(strUseTable=="")
	{
		MessageBox("The data table for the data form has not been linked to the current workflow. It may not operate correctly");
	}
	else
	{
		//-- if we do not have a matching key create a record
		var strPriKeyCol = dd.tables[strUseTable].PrimaryKey;
		if(strPriKeyCol!="")
		{
			//-- does record already exist ??
			var strParams = "tbl="+pfu(strUseTable)+"&cr="+_swdoc.OC().callref;
			app.g.submitsqs("call/common/check_bpm_dataform",strParams,false);
		}
		else
		{
			MessageBox("The data table for the data form does not have a primary key field. It may not operate correctly");
		}
		
	}
	
	//-- now open the form (use stage form if there is one otherwise use the workflow form).
	var strArgs="";	
	var strForm = _swdoc.oStage().swdataform;
	if(strForm=="")strForm = _swdoc.oWF().ext_sw_form;
	app.OpenFormForEdit(strForm, _swdoc.OC().callref, strArgs, true);
	
}

