	app.OpenFormForAdd("bpm_opencall_hist", "", strArgs, true);


}

						mbtn_mod_task_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "CreateTask", true)) return;
	// F0105843 End
	
	//-- open form at let user add a task
	var strURL = "flg_seq=0&fk_callref=" + _swdoc.OC().callref + "&fk_stage_id=" + _swdoc.OC().bpm_stage_id + "&fk_workflow_id=" + _swdoc.OC().bpm_workflow_id + "&stage_title=" + _swdoc.OC().bpm_stage_title;
	app.g.popup("bpm_add_octask",strURL,function()
	{
		sl_tasks.Refresh();
		//--F0093273 - if task history has no rows, confirm filter is set before refreshing	
		if (_swdoc.EF().sl_taskhistory.rowCount() < 1)
		{
	    	_slf(_swdoc.EF().sl_taskhistory , "_kv="+_swdoc.OC().callref);
    		_swdoc.EF().sl_taskhistory.SetRowSelected(0);
		}
		else
	    	_swdoc.EF().sl_taskhistory.Refresh();
	});
}

						mbtn_mod_auth_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "AddAuthoriser", true)) return;
	// F0105843 End

	var strFKManager = _swdoc.oCust().fk_manager;
	//if the current customer has a manager
	if(strFKManager!="")
	{
		//see if the manager is already in the authoriser list
		var intRow = app.g.sqllist_findrow_byvalue(mainform.sl_wfauth, strFKManager, "fk_auth_id");
		if(intRow>-1)
		{
			//if they are in the authoriser list already, do not pass details to On Fly Authoriser form
			strFKManager = "";
		}
	}
	//--
	//-- allow analyst to add authorisers - can only add analysts and the customers manager
	var strURL = "fk_callref=" + _swdoc.OC().callref + "&fk_workflow_id=" + _swdoc.OC().bpm_workflow_id + "&fk_stage_id=" + _swdoc.OC().bpm_stage_id + "&unanimous="+_swdoc.oStage().flg_unanimous_auth + "&fk_manager="+strFKManager;
	app.g.popup("bpm_add_ocauth",strURL, function()
	{
		sl_wfauth.Refresh();
	});	
}

						OnTimer(nTimer)
