						TabDet_OnTabSelected(nTab)
{
	var tAuth = 1;
	var tProgress=3;
	var tTasks = 2;
	var tChange = 6;
	if(nTab == tProgress)
	{
		mainform.SetTimer(1001, 10);
	}
	else if(nTab == tTasks)
	{
		_swdoc.boolRefreshTasks = true;
		_slf(_swdoc.MF().sl_tasks , "cr="+_swdoc.OC().callref+"&sid="+_swdoc.OC().bpm_stage_id);
		_swdoc.MF().sl_tasks.Refresh();
	}
	else if(nTab==tAuth)
	{
		_slf(_swdoc.MF().sl_wfauth , "cr="+_swdoc.OC().callref+"&sid="+_swdoc.OC().bpm_stage_id);
		_swdoc.MF().sl_wfauth.Refresh();
	}
	else if(nTab==tChange)
	{
		_slf(_swdoc.MF().sl_activity , "_swc_fk_callref="+_swdoc.OC().callref+"");
		_swdoc.MF().sl_activity.Refresh();
	}
	
}

