						mb_cust_OnMenuItem(strName, nItemID)
{
	var iProperties = 2;
	var iSearch=1;	
	var iChangeCompany=3;	
	
	
	if(nItemID==iSearch)
	{
		// F0105843 Start
  		// Need to replace the code that was here with a at the _swdoc level which can also be called from a Show Me menu item
  		_swdoc.search_for_customer();
		// F0105843 End
    }
    if(nItemID==iChangeCompany)
    {
        // F0105843 Start
		if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "ChangeCompany", true)) return;
		// F0105843 End
		
		app.g.search_for("company",true,"",function(sResults)
		{
			if(sResults.selectedkeys=="") return;
    
    		var arrKey = sResults.selectedkeys.split(",");
    		_swdoc.ChangeRecord("company", arrKey[0]);
		});
    }
    
    
}

						mb_cr_actions_OnMenuItem(strName, nItemID)
{
	
	var iRelease = 2;
	var iChangeCompany = 5;
	
	if(nItemID==iRelease)
	{
		// F0105843 Start
		if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RaiseChangeFromProposal", true)) return;
		// F0105843 End
		
		//-- RJC 2.4.0 FR
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = itsm_opencall_rfc.dow_note;
		
		//-- open new problem record
		app.global.LogNewCall("Change Request");
    } 
    else if(nItemID==iChangeCompany) 
    {
    
        // F0105843 Start
		if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "ChangeCompany", true)) return;
		// F0105843 End
		
    	if(dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1){
    
    		//### also used by button next to Settings/Opencall Company field!
			//###20120802 var intQResult = MessageBox("By changing the Organisation the value of Site Name, Charge Centre, Department and Contract will reset.\nAre you sure you want to continue?", MB_YESNOCANCEL|MB_ICONQUESTION);
			//###20120802 if (2 == intQResult) return; //2 is Cancel, 6 = Yes
			//###20120802 if (6 != intQResult) 
			MessageBox("The value for Site Name, Charge Centre, Department and Contract will remain, however the dropdown list will be re-populated.", MB_OKCANCEL|MB_ICONQUESTION, function(intQResult)
			{
				if (2 == intQResult) return;
				_swdoc.boolEmptySites = false;//###20120802 (6 == intQResult || 2 == intQResult);
				app.g.search_for("company",true,"",function(sResults)
				{
					if(sResults.selectedkeys=="") return;
    		
    				var arrKey = sResults.selectedkeys.split(",");
    				_swdoc.ChangeRecord("company", arrKey[0]);
				});
			});
    		
    	}
    	else
    	{
    		MessageBox("This feature is not accessible as the option to Support External Companies is not enabled.");
    	}
	}
    
}

						mbtn_rem_auth_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RemoveAuthoriser", true)) return;
	// F0105843 End

	var strConfirm = "Are you sure you want to delete the authorisation record?";
	if (confirm(strConfirm)) {
		var strPriKey = app.g.sl_getprikeyvalue(sl_wfauth, sl_wfauth.curSel);
		var strParams = "key="+pfu(strPriKey);
		if(app.g.submitsqs("call/change/remove_authoriser.delete", strParams)){	
			sl_wfauth.RemoveRow(sl_wfauth.curSel);
			sl_wfauth.SetRowSelected(0);
			sl_wfauth.Refresh(); 
			//-- update call to show that waiting auth
			app.g.update_callvalue(_swdoc.OC().callref,"1","bpm_waitingauth",true);
		}
	}

}

