						mbtn_add_activity_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "AddChangeActivity", true)) return;
	// F0105843 End

	//-- open form at let user add a task
	//var strURL = "flg_seq=0&fk_callref=" + _swdoc.OC().callref + "&fk_stage_id=" + _swdoc.OC().bpm_stage_id + "&fk_workflow_id=" + _swdoc.OC().bpm_workflow_id + "&stage_title=" + _swdoc.OC().bpm_stage_title;
	//var oForm = app.g.popup("bpm_add_octask",strURL);
	var strCIids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
	var strServiceids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
	if(strCIids!="" && strServiceids!="")
	{
		strCIids+=",";
		strCIids+=strServiceids;
	}
	var strURL = "fk_callref=" + _swdoc.OC().callref +"&ci_ids="+strCIids+"&requireBy="+_swdoc.OC().itsm_requiredbyx+"&change_type=CPR";
	app.OpenFormForAdd("change_activity", '', strURL, true, function(){
		sl_activity.Refresh();	
		_swdoc.check_act_warnings();
	});
	
}

						mbtn_remove_activity_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RemoveChangeActivity", true)) return;
	// F0105843 End
	
	var strConfirm = "Are you sure you want to delete the selected activity record?";
	if (confirm(strConfirm)) {
		var strPriKey = app.g.sl_getprikeyvalue(sl_activity, sl_activity.curSel);
		var strParams = "key="+pfu(strPriKey);
		if(app.g.submitsqs("call/change/remove_activity.delete", strParams)){	
			sl_activity.RemoveRow(sl_activity.curSel);
			sl_activity.Refresh();
			sl_activity.SetRowSelected(0, true, false);
		}
	}
	_swdoc.check_act_warnings();
}

						sl_activity_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	var strKeyValue = sl_activity.GetItemTextRaw(nRow, 'pk_auto_id');
	//-- F0091939
	if(strKeyValue=="")
		return;

	var strCIids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
	var strServiceids = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
	if(strCIids!="" && strServiceids!="")
	{
		strCIids+=",";
		strCIids+=strServiceids;
	}
	var strURL = "fk_callref=" + _swdoc.OC().callref +"&ci_ids="+strCIids+"&requireBy="+_swdoc.OC().itsm_requiredbyx+"&change_type=CPR";
	
	// F0105843 Start
	// Add an extra parameter to the URL so that the Activity Details form cannot be saved if the edit control level of the Stage does not permit
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "EditChangeActivity", false)) 
		strURL += "&viewonly=true";
	// F0105843 End
	
	app.OpenFormForEdit("change_activity", strKeyValue, strURL, true, function(){
		sl_activity.Refresh();
		_swdoc.check_act_warnings();
	});
}

