						sl_risks1_OnItemSelected(nRow)
{
	//-- NWJ
	//-- 27.11.2006
	//-- enable list menus if an item is selected	
	if(!(_swdoc.OC().status > 14))
	{
		var boolActive = (sl_risks1.rowCount() > 0);
		_een(btn_view_risk1,boolActive);
		_een(btn_del_risk1,boolActive);
		
		_een(btn_risk_up1,(nRow>0));	
		_een(btn_risk_down1,((nRow+1) < sl_risks1.rowCount()));
	}

}

						btn_risk_up1_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "EditReference", true)) return;
	// F0105843 End
	
	//-- NWJ : move uri seq up 
	
	//-- get id to move up
	var currRow = sl_risks1.curSel;
	var intUpID = sl_risks1.GetItemTextRaw(sl_risks1.curSel, 'pk_ocuri_id');

	//-- get id to down
	var intDownID = sl_risks1.GetItemTextRaw(sl_risks1.curSel-1, 'pk_ocuri_id');
	
	//-- update seq id for both 
	var strParams = "uriid=" + pfu(intUpID);
	if(app.g.submitsqp("call/change/uri_index_dec",strParams))
	{
		var strParams = "uriid=" + pfu(intDownID);
		if(app.g.submitsqp("call/change/uri_index_inc",strParams))
		{
			//-- refresh list
			sl_risks1.Refresh();
			sl_risks1.SetRowSelected(currRow-1, true, false);
		}
	}

}

						btn_risk_down1_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "EditReference", true)) return;
	// F0105843 End
	
	
	//-- NWJ : move uri seq down 
	
	//-- get id to move down
	var currRow = sl_risks1.curSel;
	var intDownID = sl_risks1.GetItemTextRaw(sl_risks1.curSel, 'pk_ocuri_id');
	var intDownSeq = sl_risks1.GetItemTextRaw(sl_risks1.curSel, 'nindex');	
	
	//-- get id to up
	var intUpID = sl_risks1.GetItemTextRaw(sl_risks1.curSel+1, 'pk_ocuri_id');
	var intUpSeq = sl_risks1.GetItemTextRaw(sl_risks1.curSel+1, 'nindex');
	
	//-- update seq id for both 
	var strParams = "uriid=" + pfu(intUpID);
	if(app.g.submitsqp("call/change/uri_index_dec",strParams))
	{
		var strParams = "uriid=" + pfu(intDownID);
		if(app.g.submitsqp("call/change/uri_index_inc",strParams))
		{
			//-- refresh list
			sl_risks1.Refresh();
			sl_risks1.SetRowSelected(currRow+1, true, false);
		}
	}
}

						btn_view_risk1_OnPressed()
{
	//-- open for edit
	var intURID = sl_risks1.GetItemTextRaw(sl_risks1.curSel, 'uri');				
	if(intURID!="")
	{
		app.global.ShellExecute(intURID);
	}

}

						btn_del_risk1_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RemoveReference", true)) return;
	// F0105843 End
	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected risk record?";
	if (confirm(strConfirm)) {
		var intSeq = sl_risks1.GetItemTextRaw(sl_risks1.curSel, "nindex");
		var strPriKey = app.g.sl_getprikeyvalue(sl_risks1, sl_risks1.curSel);
		var strParams = "key="+pfu(strPriKey);
		if(app.g.submitsqs("call/change/remove_risk.delete", strParams)){	
			sl_risks1.RemoveRow(sl_risks1.curSel);
			var intID=0;
			var nextSeq=0;
			for(var x=0; x < sl_risks1.rowCount();x++)
			{
				intThisSeq = sl_risks1.GetItemTextRaw(x, "nindex");
				if(intThisSeq>intSeq)
				{
					intID = sl_risks1.GetItemTextRaw(x, "pk_ocuri_id");
					var strParams = "uriid=" + pfu(intID);
					app.g.submitsqp("call/change/uri_index_dec",strParams);
				}
			}
			
			sl_risks1.Refresh();
			sl_risks1.SetRowSelected(0, true, false);
		}
	}		
}

