						sl_bplans1_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here
	if(!(_swdoc.OC().status > 14))
	{
		var boolActive = (sl_bplans1.rowCount() > 0);
		_een(btn_view_backout,boolActive);
		_een(btn_del_backout,boolActive);
		
		_een(btn_backout_up,(nRow>0));	
		_een(btn_backout_down,((nRow+1) < sl_bplans1.rowCount()));
	}
	
}

						btn_backout_up_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "EditBackoutStep", true)) return;
	// F0105843 End
	
	//-- NWJ : move uri seq up 
	
	//-- get id to move up
	var currRow = sl_bplans1.curSel;
	var intUpID = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel, 'pk_ocuri_id');
	var intUpSeq = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel, 'nindex');	
	
	//-- get id to down
	var intDownID = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel-1, 'pk_ocuri_id');
	var intDownSeq = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel-1, 'nindex');
	
	//-- update seq id for both 
	var strParams = "uriid=" + pfu(intUpID);
	if(app.g.submitsqp("call/change/uri_index_dec",strParams))
	{
		var strParams = "uriid=" + pfu(intDownID);
		if(app.g.submitsqp("call/change/uri_index_inc",strParams))
		{
			//-- refresh list
			sl_bplans1.Refresh();
			sl_bplans1.SetRowSelected(currRow-1, true, false);
		}
	}

}

						btn_backout_down_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "EditBackoutStep", true)) return;
	// F0105843 End
	
	//-- NWJ : move uri seq down 
	
	//-- get id to move down
	var currRow = sl_bplans1.curSel;
	var intDownID = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel, 'pk_ocuri_id');
	var intDownSeq = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel, 'nindex');	
	
