						btn_del_backout_OnPressed()
{
	// F0105843 Start
	if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RemoveBackoutStep", true)) return;
	// F0105843 End
	
	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected backout plan record?";
	if (confirm(strConfirm)) {
		var intSeq = sl_bplans1.GetItemTextRaw(sl_bplans1.curSel, "nindex");
		var strPriKey = app.g.sl_getprikeyvalue(sl_bplans1, sl_bplans1.curSel);
		var strParams = "key="+pfu(strPriKey);
		if(app.g.submitsqs("call/change/remove_backout.delete", strParams)){	
			sl_bplans1.RemoveRow(sl_bplans1.curSel);
			//-- loop through list and set seq for each to #
			var intID=0;
			var nextSeq=0;
			for(var x=0; x < sl_bplans1.rowCount();x++) {
				var intThisSeq = sl_bplans1.GetItemTextRaw(x, "nindex");
				if(intThisSeq>intSeq) {
					intID = sl_bplans1.GetItemTextRaw(x, "pk_ocuri_id");
					var strParams = "uriid=" + pfu(intID);
					app.g.submitsqp("call/change/uri_index_dec",strParams);
				}
			}
			
			sl_bplans1.Refresh();
			sl_bplans1.SetRowSelected(0, true, false);
		}
	}			
}

