						TabRisk_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	if(nTab==1)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_impactcis1.rowCount() < 1) _slf(_swdoc.EF().sl_impactcis1 ,"cr="+_swdoc.OC().callref+"&code=RFC-AFFECT");
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_kes1.rowCount() < 1) _slf(_swdoc.EF().sl_kes1 , "cr="+_swdoc.OC().callref+"&code=RFC-ERROR");
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_incs1.rowCount() < 1) _slf(_swdoc.EF().sl_incs1 , "cr="+_swdoc.OC().callref+"&code=RFC-INCIDENT");

		if (_swdoc.EF().sl_configitems1.rowCount() < 1) _slf(_swdoc.EF().sl_configitems1 , "cr="+_swdoc.OC().callref+"&code=RFC-CAUSE&type=1");
		if (_swdoc.EF().sl_services.rowCount() < 1) _slf(_swdoc.EF().sl_services , "cr="+_swdoc.OC().callref+"&code=RFC-CAUSE&type=2");

		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_srs.rowCount() < 1) _slf(_swdoc.EF().sl_srs , "cr="+_swdoc.OC().callref+"&code=REQUEST-CHANGE");

		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_cprs.rowCount() < 1) _slf(_swdoc.EF().sl_cprs , "cr="+_swdoc.OC().callref+"&code=RFC-CPR");

		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_causeinc.rowCount() < 1) _slf(_swdoc.EF().sl_causeinc , "cr="+_swdoc.OC().callref+"&code=RFC-CINCIDENT");

		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_causeprob.rowCount() < 1) _slf(_swdoc.EF().sl_causeprob ,"cr="+_swdoc.OC().callref+"&code=RFC-CPROBLEM");


		_swdoc.filter_impact_customers(_swdoc.EF());
		//oForm.sl_customerimpact1.filter	
		_ete(_swdoc.MF().tb_cnt_1,_swdoc.EF().sl_configitems1.rowCount());
		_ete(_swdoc.MF().tb_cnt_2,_swdoc.EF().sl_impactcis1.rowCount());
		_ete(_swdoc.MF().tb_cnt_3,_swdoc.EF().sl_services.rowCount());
		_ete(_swdoc.MF().tb_cnt_4,_swdoc.EF().sl_cprs.rowCount());
		_ete(_swdoc.MF().tb_cnt_5,_swdoc.EF().sl_srs.rowCount());
		_ete(_swdoc.MF().tb_cnt_6,_swdoc.EF().sl_kes1.rowCount());
		_ete(_swdoc.MF().tb_cnt_7,_swdoc.EF().sl_incs1.rowCount());
		_ete(_swdoc.MF().tb_cnt_8,_swdoc.EF().sl_customerimpact1.rowCount());
		_swdoc.MF().sl_risks1.Refresh();
	}
	else if (nTab == 3) // Affected Business Areas - load the records in the SQL List
	{
		_swdoc.MF().sl_ABAs.Refresh();
	}else if (nTab == 2) // Affected Business Areas - load the records in the SQL List
	{
		_slf(_swdoc.MF().sl_risks1 , "cr="+_swdoc.opencall.callref+"&code=RFC-RISK");
		_swdoc.MF().sl_risks1.Refresh();
	}
	
}

