						TabRel_OnTabSelected(nTab)
{
//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tCCIs = 0;
	tCService = 1;
	tCPRs = 2;
	tSRs = 3;
	tKEs = 4;	
	tIncidents = 5;
	tKB=7;
	tCauseIncidents = 6;
	tCustomers = 8;
	tTasks = 9;
	tRAKb = 10;
	
	if(nTab==tCCIs)
	{
		if (sl_configitems1.rowCount() < 1) sl_configitems1.Refresh();
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
	}
	else if(nTab==tCService)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_services.rowCount() < 1) sl_services.Refresh();
	}
	else if(nTab==tKEs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_kes1.rowCount() < 1) sl_kes1.Refresh();
	}
	else if(nTab==tKB)
	{
		if (sl_kbs1.rowCount() < 1)
		{
			sl_kbs1.Refresh();
		}
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) sl_srs.Refresh();
	}
	else if(nTab==tCPRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_cprs.rowCount() < 1) sl_cprs.Refresh();
	}	
	else if(nTab==tIncidents)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_incs1.rowCount() < 1) sl_incs1.Refresh();
	}
	else if(nTab==tCauseIncidents)
	{
		if(TabCause.tab==0)
		{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_causeinc.rowCount() < 1) sl_causeinc.Refresh();
		}
		else
		{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_causeprob.rowCount() < 1) sl_causeprob.Refresh();
		}
	}
	else if(nTab==tCustomers)
	{
		//-- load affected customers
		_swdoc.filter_impact_customers(_swdoc.EF());
		_swdoc.EF().sl_customerimpact1.Refresh();
		//-- load affected orgs
		_swdoc.filter_impact_orgs(_swdoc.EF());  
	}
	else if(nTab==tTasks)
	{
		if (sl_taskhistory.rowCount() < 1)
		{
			sl_taskhistory.Refresh();
			sl_taskhistory.SetRowSelected(0);
		}
	}
	else if(nTab==tRAKb)
	{
		if (sl_rakb.rowCount() < 1)
		{
			sl_rakb.Refresh();		
		}
	}	
	
	
		
}

