						btn_srch_rel1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   var strURL = "canreset=1";  

    app.itsm.search_calls("RFC",strURL, true, function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_rels = oResults.selectedkeys.split(",");  
		var strText = "This change proposal has been associated with the following change requests\n\n";
		var boolRELs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rels.length;x++)
    	{
        	var rel_callref = array_rels[x];
        	var rel_callref_text = app.g.pad_callref(rel_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_cprs,rel_callref,0);
			if (rowPos == -1)
			{ 		
				boolRELs=true;
				//-- call g method to link problem to incident
				if(app.g.relate_calls(rel_callref,_swdoc.OC().callref,'RFC-CPR'))
				{
					strText += "Change Request (" + rel_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRelText = "The change proposal (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this change request.";
					app.g.new_diaryevent(rel_callref,strRelText,"Change Management","Change Proposal Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolRELs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Change Request Association");
			sl_cprs.Refresh();
		}
    });
}

