						mbtn_ke_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	var iCauseCI = 2;
	var iImpactCI = 3;
	
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;	
	
	//-- NWJ - unlink incident
	if(nItemID == iDisassociate)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_kes1,0,",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'RFC-ERROR'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This change request has been dis-associated from the following known errors\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Known Error (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strKEText = "The change request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this Known Error";
					app.g.new_diaryevent(arrCallrefs[x], strKEText,"Change Management","Change Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Known Error Dis-association");
			}
		
			sl_kes1.Refresh();	
			sl_kes1_OnItemSelected(0);

			//-- get linked incidents - and remove them
			var strIncs = app.g.get_slave_calls(strCallRefs,"PROBLEM-INCIDENT");
			if(strIncs!="0") _swdoc.remove_change_incidents(strIncs,true);
			_swdoc.remove_selected_ke_cis(strCallRefs)
		}	
	}
	else if(nItemID == iImpactCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_kes1,0,",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "PROBLEM-AFFECT", "");
		_swdoc.link_affect_cis_to_change(oResultCIs.keys,oResultCIs.text);
	}
	else if(nItemID == iCauseCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_kes1,0,",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "PROBLEM-CAUSE", "");
		_swdoc.link_cause_cis_to_change(oResultCIs.keys,oResultCIs.text);
	}	

}

						btn_srch_kes1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass, true))return;

    var strURL = "canreset=1";  

    app.itsm.search_calls("KE",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_kes = oResults.selectedkeys.split(",");  
		var strText = "This change request has been associated with the following known errors\n\n";
		var boolKEs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_kes.length;x++)
    	{
        	var kes_callref = array_kes[x];
        	var kes_callref_text = app.g.pad_callref(kes_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_kes1,kes_callref,0);
			if (rowPos == -1)
			{ 		
				boolKEs=true;
				//-- call g method to link problem to incident
				if(app.g.relate_calls(_swdoc.OC().callref,kes_callref,'RFC-ERROR'))
				{
					strText += "Known Error (" + kes_callref_text + ") associated\n";
					
					//-- update known error diary
					strKesText = "The change Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this known error.";
					app.g.new_diaryevent(kes_callref,strKesText,"Change Management","Change Association");
				}
			}
    	} //-- eof for
	
		//-- create change call diary update then reload list
		if (boolKEs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Known Error Association");
			sl_kes1.Refresh();
	
			//-- Now check if we should link ke incidents to the change
			if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorIncidents")==1)
			{
				//-- if dont have info get it otherwise use info we go above
				var strIncs = app.g.get_slave_calls(oResults.selectedkeys,"PROBLEM-INCIDENT");
				if(strIncs!="0")_swdoc.link_incidents_to_change(strIncs);
			}
	
			
			//-- Now check if we should link new ke ci affect items as being rfc affect cis
			if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorImpactCIs")==1)
			{
				//-- if dont have info get it otherwise use info we go above
				var oRes = app.cmdb.get_call_ci_info(oResults.selectedkeys , "PROBLEM-AFFECT", "");
				if(oRes.keys!="")_swdoc.link_affect_cis_to_change(oRes.keys,oRes.text);
			}
	
			//-- Now check if we should link new ke cause ci items as being rfc cause cis
			if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorCauseCIs")==1)
			{
				//-- if dont have info get it otherwise use info we go above
				var oRes = app.cmdb.get_call_ci_info(oResults.selectedkeys , "PROBLEM-CAUSE", "");
				if(oRes.keys!="")_swdoc.link_cause_cis_to_change(oRes.keys,oRes.text,oRes.type);
			}		
		}
    });
}

