						mbtn_srs_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	
	//-- NWJ - unlink release
	if(nItemID == iDisassociate)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_srs,0,",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'REQUEST-CHANGE'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This change request has been dis-associated from the following service requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Service Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRELText = "The change request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this service request";
					app.g.new_diaryevent(arrCallrefs[x], strRELText,"Change Management","Change Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Service Request Dis-association");
			}
		
			sl_srs.Refresh();
			sl_srs_OnItemSelected(0);
		}	
	}

}

						btn_srch_srs_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";  

    app.itsm.search_calls("SR",strURL, true, function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_srs = oResults.selectedkeys.split(",");  
		var strText = "This change request has been associated with the following service requests\n\n";
		var boolSRs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_srs.length;x++)
    	{
        	var sr_callref = array_srs[x];
        	var sr_callref_text = app.g.pad_callref(sr_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_srs,sr_callref,0);
			if (rowPos == -1)
			{ 		
				boolSRs=true;
				//-- call g method to link problem to incident
				if(app.g.relate_calls(_swdoc.OC().callref,sr_callref,'REQUEST-CHANGE'))
				{
					strText += "Service Request (" + sr_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRelText = "The change Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this Service Request.";
					app.g.new_diaryevent(sr_callref,strRelText,"Change Management","Change Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolSRs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Service Request Association");
			sl_srs.Refresh();
		}
    });
}

						sl_srs_OnItemSelected(nRow)
