	// TODO: Add your event handler code here

	var boolActive = (sl_srs.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_srs);	
	_een(mbtn_srs_views,(selCount==1));	
	_een(mbtn_srs_actions,boolActive);

}

						mbtn_cci_views2_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_cci_actions2_OnMenuItem(strName, nItemID)
{

	var iRemove = 1;	
	if(nItemID==iRemove)
	{
		//-- remove CI
		// F0105843 Start
		//if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;	// This is replaced by the new function, which includes the Can Edit check
		if (!app.itsm.has_rfc_reqd_edit_level(_swdoc.oStage().edit_level_reqd, _swdoc.RFC_EXT().raised_by_analystid, "RemoveService", true)) return;
		// F0105843 End
		_swdoc.remove_selected_cause_services();
		_swdoc.MF().sl_ABAs.Refresh(); // do this to refresh the Affected Business Areas tab on main Details form
	}


}

