						sl_rakb_OnItemDoubleClicked(nRow, nCol)
{
	var strKeyValue = app.g.sl_getcolvalue(sl_rakb, nRow, "docref");
	app.g.open_article(strKeyValue);

}

						sl_rakb_OnItemSelected(nRow)
{
	var boolActive = (sl_rakb.rowCount()>0);
	var selCount = app.g.sl_getselected_count(sl_rakb);	
	_een(mbtn_inc_views5, (selCount==1));
	_een(mbtn_inc_actions4, boolActive);
}

						TabImpact_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	if(nTab==1)
	{
		_swdoc.filter_impact_orgs(_swdoc.EF());
		_swdoc.EF().sl_orgimpact.Refresh();
	}

}

						mbtn_cpr_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	
	//-- NWJ - unlink release
	if(nItemID == iDisassociate)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_cprs,0,",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'RFC-CPR'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This change proposal has been dis-associated from the following change requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Change Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRELText = "The change proposal (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this change request";
					app.g.new_diaryevent(arrCallrefs[x], strRELText,"Change Management","Change Proposal Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Change Management","Change Request Dis-association");
			}
		
			sl_cprs.Refresh();
			sl_cprs_OnItemSelected(0);
		}	
	}

}

						mbtn_cpr_views_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	
	if(nItemID == iDetails)
	{
		if(sl_cprs.curSel > -1)sl_cprs.EditRecord(sl_cprs.curSel);
	}
}

						sl_cprs_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here

	var boolActive = (sl_cprs.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_cprs);	
	_een(mbtn_cpr_views,(selCount==1));	
	_een(mbtn_cpr_actions,boolActive);
}
