						OnUpdateCall(nCallRef)
{
	// TODO: Add your event handler code here
	var strArgs = "in_callrefs=" + _swdoc.OC().callref;
	app.OpenForm("itsmEfUpdateCallForm_OLT", strArgs, true);

	return false;
}

						OnDataChanged()
{
	// TODO: Add your event handler code here
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			_een(_swdoc.MF().btn_actionsts, true);
	}


}

						OnFormLoaded()
{

	//-- pad out callref
	_ete(tb_parentref,app.g.pad_callref(opencall.bpm_parentcallref));
		
	//-- load description
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref, boolCache, tb_updatetext);
	
	_swdoc.oRecIncident = app.g.get_record("opencall", opencall.bpm_parentcallref);	
    //-- F0095600
    _swdoc.oRecIncident.fixbyx = app.g.fix_epoch(_swdoc.oRecIncident.fixbyx);
	_swdoc.oRecIncident.respondbyx = app.g.fix_epoch(_swdoc.oRecIncident.respondbyx);
	_swdoc.oRecIncident.logdatex = app.g.fix_epoch(_swdoc.oRecIncident.logdatex);
	app.g.load_form_fields(_swdoc.oRecIncident, mainform, "incident");
	
	boolOLAImpactsParent = (app.g.fix_epoch(_swdoc.OC().fixbyx) > new Number(_swdoc.MF().s_incident__fixbyx.text));
	_eva(_swdoc.MF().lb_impact,(boolOLAImpactsParent)?"2":"1");
	
	_ete(_swdoc.MF().lbl_oc_callref,_swdoc.OC().h_formattedcallref);
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			_een(_swdoc.MF().btn_actionsts, true);
	}
	
}

						mb_inview1_OnMenuItem(strName, nItemID)
{
	var iViewInc = 1;
	
	if(nItemID == iViewInc)
	{
		app.global.OpenCallDetailsView(_swdoc.oRecIncident.callref);
	}


}

						btn_actionsts_OnPressed()
{
	// TODO: Add your event handler code here
	var xmlmc = new XmlMethodCall;

	// Set values for the Input parameters - Parameter values must be set in the same order they are expected by the method.
	xmlmc.SetParam("callref", _swdoc.OC().callref); 				
	
	// Invoke the VPME method LogCall.
	if(xmlmc.Invoke("VPME", "clearActionStatus"))
	{
    	// Success, lets access output parameters
    	_een(_swdoc.MF().btn_actionsts, false);
	}
	else
	{
    	// Error
    	MessageBox(xmlmc.GetLastError());
	}


}
