//--
//-- based on the current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032
	
	//-- NWJ
	//-- assign _swdoc to cfg array (so we can refresh tasks list from task forms
	app.cfg.allocaterfcdoc(_swdoc.OC().callref,_swdoc)
		
	_swdoc.strOriginalSLA = _swdoc.opencall.priority;
	
	// no process so do not show process tabs
	if(_swdoc.OC().bpm_workflow_id=="")
	{
		_swdoc.MF().TabDet.ShowTabItem(4, false);
		_etab(_swdoc.MF().TabDet,0);
		return true;
	}

	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}
	
	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;

	//-- if user can add tasks and this stage allows it
	var strParams = "cr="+pfu(_swdoc.OC().callref)+"&sid="+_swdoc.oStage().pk_stage_id;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);

	

    var boolHasTasks = intTaskCount>0;
    var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(PG_BPM, BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));

	_een(_swdoc.MF().mbtn_mod_task1,boolAddTasks);
	_swdoc.MF().TabDet.ShowTabItem(6,boolShowTasks);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}
	
	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(PG_BPM, BPM_ADDAUTH,app.session.dataDictionary)) )
	//MessageBox(_swdoc.oStage().flg_unanimous_auth);
	if(_swdoc.oStage().flg_unanimous_auth==1)
	{
		_evi(_swdoc.MF().authWeighting1,false);
	}	
	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(5, boolRequiresAuth||boolAddAuths);
	
	//if we require authorisation, or disable manual, do not enable status

	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	_een(_swdoc.MF().lb_wf_state1,(!(boolTaskStop|| boolRequiresAuth)));
	
	_evi(_swdoc.MF().mbtn_mod_auth1,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF
	
	//-- if we have data form show button
	_een(_swdoc.MF().btn_bpm_form1,((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));
	
	//-- select tab 0 or 1 depending on auth
	_etab(_swdoc.MF().TabDet,(boolRequiresAuth)?5:0);
		

	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);
	return true;
}

//-- nwj - 15.08.2008 - filter the customers company sites - remember those that have been checked
function filter_company_sites()
{
	
	if(_swdoc.oCompany().pk_company_id=="")
		_slf(_swdoc.MF().sl_sites,"_swc_fk_company_id=-1");	
	else
		_slf(_swdoc.MF().sl_sites,"_swc_fk_company_id=" + _swdoc.oCompany().pk_company_id);
	_swdoc.MF().sl_sites.Refresh();
	//-- get impacted sites and check them
	var arrFK_CI_IDS = app.cmdb.get_call_cis(_swdoc.OC().callref , "PROBLEM-SITE","").split(",");
	
	//--
	//-- loop through impacted sites and create link
	for (var x=0;x < arrFK_CI_IDS.length;x++)
	{
		app.g.sqllist_checkrow_byvalue(_swdoc.MF().sl_sites,arrFK_CI_IDS[x],"fk_cmdb_id");
	}
}

//-- given an analyst object (from app.global.js) load raised by values
//-- for the problem
function set_prb_analyst(oAnalyst)
{
	if(!oAnalyst) return false;
	
	_swdoc.PRB().raised_by_analystid = oAnalyst.id;
	_swdoc.PRB().raised_by_name = oAnalyst.name;
	_swdoc.PRB().raised_by_group = oAnalyst.supportgroup;
	_swdoc.PRB().raised_by_telephone =  oAnalyst.telephone;
	_swdoc.PRB().raised_by_email = oAnalyst.email;	
		
	_swdoc.UpdateFormFromData();
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_cis_to_problem(strLinkCIs,strLinkCIsText,strCIType)
{
	//-- loop through selected CIs
	var strText = "";
	var strServiceText = "";
	var arrayCIkeys = strLinkCIs.split(",");
	var arrayCItext = strLinkCIsText.split(",");	
	var arrayCItypes = strCIType.split(",");	

	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
        var ci_type_text = arrayCItypes[x];
        
		// -- SG, 02/07/2012, F0103324
		// Build array of CI's that have the Bespoke Terms field populated
		var rec = app.g.get_record("config_itemi", pk_ci_id);
		if(rec.recordresolvedmsg != '')
		{
    		bespokeTermsArray[countBespokeTerms] = new Array(2);
    		bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    		bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    		countBespokeTerms++;
		}
		// End of array building
		
		//-- check that CI is not already in the list
		if(ci_type_text.indexOf("Service")>-1)
  		{
 			//-- check that CI is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,0);
			if (rowPos == -1)
			{ 
				//-- Relate the CI to the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'PROBLEM-CAUSE'))
				{
					if(strServiceText!="")strServiceText += ",";
					strServiceText += ci_item_text;
				}
			}
  		}else
  		{
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_configitems1,pk_ci_id,0);
			if (rowPos == -1)
			{ 
				//-- Relate the CI to the call
				if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'PROBLEM-CAUSE'))
				{
					if(strText != "") strText +=",";
					strText += ci_item_text;
				}
			}
		}
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following problem configuration items were added to this problem.\n" + strText;
		var strActionSource = "Problem Management";
		var strCode			= "Linking Configuration Items";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_configitems1.Refresh();
		_swdoc.EF().sl_configitems1.SetRowSelected(0);
		_swdoc.EF().sl_configitems1_OnItemSelected(0);
		
	}
	
		//-- apply call diary update for added CIs (if we have any to add)
	if (strServiceText!="")
	{
		strServiceText = "The following problem services were added to this problem.\n" + strServiceText;
		var strActionSource = "Problem Management";
		var strCode			= "Linking Services";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strServiceText,strActionSource,strCode);
		_swdoc.EF().sl_services.Refresh();
		_swdoc.EF().sl_services.SetRowSelected(0);
		_swdoc.EF().sl_services_OnItemSelected(0);
		
	}
	// -- SG, 02/07/2012, F0103324
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_services_to_problem(strLinkCIs,strLinkCIsText)
{
	//-- loop through selected CIs
	var strText = "";
	var arrayCIkeys = strLinkCIs.split(",");
	var arrayCItext = strLinkCIsText.split(",");	
	
	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Service";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_services,pk_ci_id,0);
		if (rowPos == -1)
		{ 
			// -- SG, 02/07/2012, F0103324
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building
			
			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'PROBLEM-CAUSE'))
			{
				if(strText != "") strText +=",";
				strText += ci_item_text;
			}
		}
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following problem services were added to this problem.\n" + strText;
		var strActionSource = "Problem Management";
		var strCode			= "Linking Services";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_services.Refresh();
		_swdoc.EF().sl_services.SetRowSelected(0);
		_swdoc.EF().sl_services_OnItemSelected(0);
		
	}
	
	// -- SG, 02/07/2012, F0103324
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//-- given a ci or list of cis link it to problem affect list
function link_affect_cis_to_problem(strLinkedCIs,strLinkCIsText)
{
	//-- loop through selected CIs
	var strText = "";
	var arrayCIkeys = strLinkedCIs.split(",");
	var arrayCItext = strLinkCIsText.split(",");
	
	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
		
		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_impactcis1,pk_ci_id,0);
		if (rowPos == -1)
		{ 
			// -- SG, 02/07/2012, F0103324
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building
			
			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'PROBLEM-AFFECT'))
			{
				if(strText!="")strText += ",";
				strText += ci_item_text;
			}
		}
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following configuration items have been identified as being impacted by this problem.\n" + strText;
		var strActionSource = "Problem Management";
		var strCode			= "Linking Configuration Items";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_impactcis1.Refresh();
		_swdoc.EF().sl_impactcis1.SetRowSelected(0);
		_swdoc.EF().sl_impactcis1_OnItemSelected(0);
		
	}	
	_swdoc.filter_impact_customers(_swdoc.EF(),true);
	// -- SG, 02/07/2012, F0103324
	bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, itemType);
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_incident_cis(strSelectedIncs)
{
	var array_RemoveImpactCIS = new Array();
	if(_swdoc.EF().sl_impactcis1.rowCount()<1) return;
	
	//--
	//-- get linked inc cis and see if they are linked to problem
	var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
	if(strLinkedCIs=="0")return;

	var arrayCIS = strLinkedCIs.split(",");
	for(var x=0;x<arrayCIS.length;x++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_impactcis1,arrayCIS[x],"pk_auto_id")!=-1)	array_RemoveImpactCIS[array_RemoveImpactCIS.length++] = arrayCIS[x];			
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveImpactCIS.length>0)
	{
		if(confirm("There are associated impact configuration items, that are linked to the incidents you are removing. Do you want to remove these items as well?"))
		{
			//-- unselect all , the select those we want to remove
			app.g.sl_unselect(_swdoc.EF().sl_impactcis1);
			for(var y=0;y<array_RemoveImpactCIS.length;y++)
			{
				var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_impactcis1,array_RemoveImpactCIS[y],"pk_auto_id");
				if(intRow>-1)
				{
					_swdoc.EF().sl_impactcis1.SetRowSelected(intRow, true, true);
				}
			}
			_swdoc.remove_selected_impact_cis();
		}
	}
}


//-- remove selected cause cis
function remove_selected_cause_cis()
{
    //-- NWJ - 15.03.2004
    //-- Remove Selected config items
	var strCIs = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,"pk_auto_id",",");
	if(strCIs!= "")
	{
    	//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    	if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strCIs,'PROBLEM-CAUSE'))
    	{
    		//-- update incident diary
    		var strCIidentifiers = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,1,",");
			var strText     	= "The following problem configuration items were removed from this problem.\n" + strCIidentifiers;
			var strActionSource = "Problem Management";
			var strCode			= "Remove Configuration Items";
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    	}			

	    //-- re-filter list	
		_swdoc.EF().sl_configitems1.Refresh();
		_swdoc.EF().sl_configitems1.SetRowSelected(0);
		_swdoc.EF().sl_configitems1_OnItemSelected(0);
		
    }
}

