{
    //-- NWJ - 15.03.2004
    //-- Remove Selected config items
	var strCIs = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_services,"pk_auto_id",",");
	if(strCIs!= "")
	{
    	//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
