						OnFormLoaded(strFormName, strCallclass)
{
	// TODO: Add your event handler code here

	//--TK Performance 
	if(oSettings.get_setting("qatime")==1)
	{
		_swdoc.endTime = new Date().getTime();
		var intTime = _swdoc.endTime - _swdoc.startTime;
		app.g.qa_log_time("Problem Details Loaded", intTime);
	}
	//--F0110887 Start
	if(_swdoc.oCompany().pk_company_id != "")
	{
		var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().SUPPORTEXPIRYX);
		app.g.checkOrgSupport(_swdoc.oCompany().pk_company_id, intExpiryEpoch, true);
	}
	//--F0110887 End
	
	// Refresh Auth list
	_swdoc.MF().sl_wfauth1.Refresh();
	
}

						function set_buttons()
{    
    //-- NWJ
    //-- 08.04.2004
    //-- determine which buttons to display to the user


    //-- closed or cancelled
    if (_swdoc.can_update == 0)
    {
        //-- show close button
        //-- hide close and accpet button
        _evi(btn_assign,false);
        _evi(btn_cancel,false);
        _evi(btn_accept,false);
        _evi(btn_save,false);
        _evi(btn_convert,false);
        _evi(lbl_actions,false);

        return;
    }

    //-- if unaccepted & not resolved or closed or cancelled show the accept and assign button
    var currStatus = _swdoc.opencall.status;
    _evi(btn_convert,false);
    
    //-- accepted
    if (currStatus == 1)
    {
        //-- show resolve, re-assign, cancel button button
        //-- hide close and accept button
        _evi(btn_assign,true);
        _evi(btn_cancel,true);
        _evi(btn_accept,false);
        _een(btn_save,false);
        _evi(btn_convert,true);
    }

    //-- NWJ - 07.05.2004 - THis will never happen in problem now
    //-- resolved
    if (currStatus == 6)
    {
        //-- show close button
        //-- hide close and accpet button
        _evi(btn_assign,false);
        _evi(btn_cancel,false);
		_evi(btn_accept,false);
        _een(btn_save,false);
    }

    //-- unaccepted/unassigned
    if ((currStatus == 3) || (currStatus == 2))
    {
        //-- show cancel, accept button
        _evi(btn_accept,true);
        _evi(btn_assign,true);
        _evi(btn_cancel,true);
		_een(btn_save,false);
    }

    //-- closed or cancelled
    if (currStatus > 15)
    {
        //-- show close button
        //-- hide close and accpet button
        _evi(btn_assign,false);
		_evi(btn_cancel,false);
        _evi(btn_accept,false);
        _evi(btn_save,false);
        _evi(lbl_actions,false);
    }
    
    return true;
}

						OnFormLoaded()
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		var strParams = "fk_workflow_id="+pfu(_swdoc.OC().bpm_workflow_id);
   		var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
		while(oRS.Fetch())
		{
			var strBinding = app.g.get_field(oRS,"fieldname");
			arrBinding = strBinding.split(".");
			_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
		}
	}//-- EOF F0105032
	
	_swdoc.setup_process_fields(false);
	_swdoc.setup_desc_tabs();

	if(app.g.remote_support_enabled())
	{
		_evi(_swdoc.MF().btn_remotesupport, true);	
	}

	_een(_swdoc.MF().btn_convke,(itsm_opencall_problem.wa_info!=""));
	
	//-- load related incidents - have to do this so we can get affect customers
	_swdoc.EF().sl_configitems1.Refresh();

	//-- filter and check impacted sites
	_swdoc.filter_company_sites();
	_swdoc.strOrigCompany = _swdoc.oCompany().companyname;
	
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	var strSupportExternalCompanies = ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1));
	_een(_swdoc.MF().lb_company,strSupportExternalCompanies);
	if(!strSupportExternalCompanies)
	{
		//-- check if we should hide
		_evi(_swdoc.MF().lb_company,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));
		//alter filter if we do not support external
		_slf(_swdoc.MF().lb_oc_cc,"");
		_slf(_swdoc.MF().fld_oc_dept,"");
	}
	_evi(_swdoc.MF().lbl_company,_swdoc.MF().lb_company.visible);
	
	//-- eof hide / enable	
	_swdoc.strOriginalSLA = _swdoc.opencall.priority;
	_swdoc.strOrigProbcode = _swdoc.opencall.probcode;
	_swdoc.intLastOCStatus = _swdoc.OC().status;
	
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_last_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_ongoing);

	_ete(_swdoc.MF().lbl_oc_callref,_swdoc.OC().h_formattedcallref);
	
	//-- Show or hide contract field based on whether we support external companies
	_evi(_swdoc.MF().lbl_contract, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	_evi(_swdoc.MF().lb_contract, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	//-- Filter Contract List for Selected company
	app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract, _swdoc.OC().itsm_sladef);
	
	_swdoc.load_diary_textfields();	
	
	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			_een(_swdoc.MF().btn_actionsts, true);
	}
	//--F0108045
	app.g.add_me_document("REQUEST", _swdoc.OC().callref, _swdoc);
}

						tb_aprprofile_OnLeftClick()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass))return;
	
	//--
	app.g.pick_problemprofile(_swdoc.PRB().inc_profilecode,"", false, function(strCode){
		if(strCode){
			_swdoc.PRB().inc_profilecode = strCode;
		}
		else if(strCode===""){
			_ete(tb_aprprofile, "");
			_swdoc.PRB().inc_profilecode = "";
		}	
		_eva(_swdoc.MF().tb_aprprofile, _swdoc.PRB().inc_profilecode); // because the field wasn't refreshing without this
		_swdoc.UpdateFormFromData();
	});
	return false;
}

						tb_aprprofile_OnRightClick()
{
	//-- clear profile
	_swdoc.PRB().inc_profilecode = "";
	_ete(tb_aprprofile, "");
	_swdoc.UpdateFormFromData()


	return true;
}

