						sl_sites_OnItemChecked(nRow, bChecked)
{
	var intCIkey = sl_sites.GetItemTextRaw(nRow, 'fk_cmdb_id');
	var strSite = sl_sites.GetItemTextRaw(nRow, 'site_name');
	
	if (intCIkey && strSite)
	{
		if(bChecked)
		{
			app.cmdb.relate_ci_to_call(_swdoc.OC().callref, intCIkey, 'PROBLEM-SITE');
			var strText = "The following site as been reported as experiencing this problem : " + strSite;	
			var strCode	= "Site Added";		
			
			
		}
		else
		{
			app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,intCIkey,'PROBLEM-SITE');
			var strText = "The following site is no longer experiencing this problem : " + strSite;		
			var strCode	= "Site Removed";		
			
		}
		
		//-- apply diary
		var strActionSource = "Problem Management";
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);	
		
	}

}

						sl_sites_OnItemDoubleClicked(nRow, nCol)
