			var strText = "The following site as been reported as experiencing this problem : " + strSite;	
			var strCode	= "Site Added";		
			
			
		}
		else
		{
			app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,intCIkey,'PROBLEM-SITE');
			var strText = "The following site is no longer experiencing this problem : " + strSite;		
			var strCode	= "Site Removed";		
			
		}
		
		//-- apply diary
