		_ete(_swdoc.MF().tb_no_of_custs,_swdoc.EF().sl_customerimpact1.rowCount());	
		_ete(_swdoc.MF().tb_no_of_cis,_swdoc.EF().sl_configitems1.rowCount() + _swdoc.EF().sl_services.rowCount());
		if(_swdoc.MF().sl_childitems.rowCount()==0)
		{
			var strChildKeys = "";
			var strCIKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems1, 'pk_auto_id', ",", "");
			var strServiceKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
			if(strServiceKeys!="")
			{
				if(strCIKeys!="")strCIKeys+=",";
				strCIKeys+=strServiceKeys;
			}
			
			if(strCIKeys!="")
			{
				_slf(_swdoc.MF().sl_childitems,"cis="+strCIKeys);
				_swdoc.MF().sl_childitems.Refresh();
			}
		}

	}


}

