						TabDet_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	var tProgress=7;
	var tAuth = 5;
	var tTasks=6;
	if(nTab == tProgress)
	{
		mainform.SetTimer(1001, 10);
	}
	else if(nTab==4)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intCount = app.g.sqs_rowcount("call/common/cnt_progress_items","wf="+pfu(_swdoc.OC().bpm_workflow_id));
			if(intCount>0)
				_een(mainform.btn_bpm_progress,true);
		}
			
	}
	else if(nTab == tTasks)
	{
		_swdoc.boolRefreshTasks = true;
		_swdoc.MF().sl_tasks.Refresh();
	}
	else if(nTab==tAuth)
	{
		_swdoc.MF().sl_wfauth1.Refresh();
	}
	
}

