						btn_actionsts_OnPressed()
{
	// TODO: Add your event handler code here
	var xmlmc = new XmlMethodCall;

	// Set values for the Input parameters - Parameter values must be set in the same order they are expected by the method.
	xmlmc.SetParam("callref", _swdoc.OC().callref); 				
	
	// Invoke the VPME method LogCall.
	if(xmlmc.Invoke("VPME", "clearActionStatus"))
	{
    	// Success, lets access output parameters
    	_een(_swdoc.MF().btn_actionsts, false);
	}
	else
	{
    	// Error
    	MessageBox(xmlmc.GetLastError());
	}


}

						lb_environment_OnLeftClick()
{
	 // ES - F0111761 - Ability to specify an environment within call forms
     var strSimpleListId = "ENVIRONMENT";
     var p = {};
     p.id = strSimpleListId;
     var dbValues = app.g.get_sqrecordset("GENERAL/get.simple_list", p);
     var arrEnvironments = new Array();
     var strEnvironments = "";
     var splitResult = "";
     var intCount = 0;
     // Store ENVIRONMENT values from swlists in an array
     while (dbValues.Fetch())
     {
          arrEnvironments[intCount] = app.g.get_field(dbValues, "value");
          intCount++;          
     }
     var strTemp = "";
     // Add | at the end of each value to set up the picklist
     for (var i=0; i<arrEnvironments.length;i++)
     {
          strTemp = arrEnvironments[i];
          splitResult = strTemp.split(",");
          strEnvironments += splitResult[0];
          strEnvironments += "|";
     }
     _swdoc.MF().fld_environment.pickList = strEnvironments;
     // END - F0111761;


	return true;
}

						lb_environment_OnValueChanged(strValue)
{
	_swdoc.opencall.environment = strValue;

}

