}

						TabRel_OnTabSelected(nTab)
{
//-- NWJ - 28.11.2006
	//-- Handle tab clicking
	tCCIs = 0;
	tCServices = 1;
	tRFCs =2;
	tICIs = 3;
	tIncidents = 4;
	tKB = 5;
	tCustomers = 6;
	tOrgs = 7;	
	tRAKb = 8;

	if(nTab==tCCIs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_configitems1.rowCount() < 1) 
		{
			_swdoc.EF().sl_configitems1.Refresh();
			sl_configitems1.SetRowSelected(0, true);
			sl_configitems1_OnItemSelected(0);
		}
	}
	else if(nTab==tCServices)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_services.rowCount() < 1) 
		{
			_swdoc.EF().sl_services.Refresh();
			sl_services.SetRowSelected(0, true);
			sl_services_OnItemSelected(0);
		}
	}
	else if(nTab==tKB)
	{
		if (_swdoc.EF().sl_kbs1.rowCount() < 1)
		{
			_swdoc.EF().sl_kbs1.Refresh();
		}
	}
	else if(nTab==tICIs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_impactcis1.rowCount() < 1) 
		{
			_swdoc.EF().sl_impactcis1.Refresh();
			sl_impactcis1.SetRowSelected(0, true);
			sl_impactcis1_OnItemSelected(0);
		}
	}
	else if(nTab==tRFCs)
	{
		if(TabChanges.tab==0)
		{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (_swdoc.EF().sl_rfccause.rowCount() < 1) 
			{
				_swdoc.EF().sl_rfccause.Refresh();
			}
		}else
		{
			if (_swdoc.EF().sl_rels1.rowCount() < 1) 
			{
				_swdoc.EF().sl_rels1.Refresh();
			}		
		}
	}
	else if(nTab==tIncidents)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (_swdoc.EF().sl_incs1.rowCount() < 1) 
		{
			_swdoc.EF().sl_incs1.Refresh();
			sl_incs1.SetRowSelected(0);
			sl_incs1_OnItemSelected(0);
		}
	}
	else if(nTab==tCustomers)
	{
		//-- load affected customers
		//-- get list of customers impacted by these incidents and refresh cust impact list
		_swdoc.EF().sl_incs1.Refresh();
		_swdoc.filter_impact_customers(_swdoc.EF());
		_swdoc.EF().sl_customerimpact1.Refresh();
	}
	else if(nTab==tOrgs)
	{
		//-- load affected organisations
		//-- get list of orgs impacted by these incidents and refresh org impact list
		_swdoc.EF().sl_incs1.Refresh();
		_swdoc.filter_impact_orgs(_swdoc.EF());
		_swdoc.EF().sl_orgimpact.Refresh();
	}	
	else if(nTab==tRAKb)
	{
		if (sl_rakb.rowCount() < 1)
		{
			_swdoc.EF().sl_rakb.Refresh();
		}
	}	
	
}

