						mbtn_ici_views1_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_impactcis1,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_impactcis1,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_ici_actions1_OnMenuItem(strName, nItemID)
{

	var iRemove = 1;	
	var iRemoteSupport = 2;
	
	if(nItemID==iRemove)
	{
		//-- remove CI
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
		_swdoc.remove_selected_impact_cis();
	}
	else if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(sl_impactcis1,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI, _swdoc.OC().callref);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}

}

						btn_search_ici1_OnPressed()
{
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

    //-- NWJ 08.03.2004
    //-- open CI search form in select mode
	var strURL = "canreset=1&fk_company_id=" + pfu(_swdoc.oCompany().pk_company_id);
	var sResults = app.cmdb.search_ci(true,strURL,function()
	{
		if (sResults.selectedkeys=="") return;
	
		_swdoc.link_affect_cis_to_problem(sResults.selectedkeys,sResults.selectedtext);
	});
}

