						OnFormLoaded(bDataReloaded)
{
	TabRel.ShowTabItem(7,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	_etab(TabRel,0);

	//-- Show appropriate KB Tab
	var tTab = _swdoc.EF().TabRel.tab;
	if(!app.g.use_sw_knowledgebase())
	{
		if(strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			_swdoc.EF().TabRel.ShowTabItem(8,true);
			_swdoc.EF().TabRel.ShowTabItem(5,false);
			_etab(_swdoc.EF().TabRel, tTab);
		}
	}
	else
	{
		_swdoc.EF().TabRel.ShowTabItem(8,false);
		_swdoc.EF().TabRel.ShowTabItem(5,true);
		_etab(_swdoc.EF().TabRel, tTab);
	}


}

