						mbtn_inc_actions2_OnMenuItem(strName, nItemID)
{
	var iSearch = 1;
	var iRelated = 2;
	var iChildren = 3;
	var iParents = 4;
	var iProfile = 5;
	
	//-- can they change?
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	var funcFinalProcessing = function(oResults)
	{
		//only reach here if there are incidents to associate
		var array_inc = oResults.selectedkeys.split(",");  
 		var strText = "This problem has been associated with the following incidents\n\n";
		var boolIncs = false;
		//-- loop through selected incidents and link them
    	for (var x=0;x<array_inc.length;x++)
    	{
        	var inc_callref = array_inc[x];
        	var inc_callref_text = app.g.pad_callref(inc_callref);
	
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_incs1,inc_callref,0);
			if (rowPos == -1)
			{ 
				boolIncs = true;
				//-- call g method to link problem to incident
				if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'PROBLEM-INCIDENT'))
				{
					strText += "Incident (" + inc_callref_text + ") associated\n";
					
					//-- update ke or problem diary
					strIncText = "The problem (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this incident."
					app.g.new_diaryevent(inc_callref,strIncText,"Problem Management","Problem Association");
				}
			}
	
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if(boolIncs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Problem Management","Incident Association");
			sl_incs1.Refresh();
			
			//-- Now check if we should link incident ci items as being affected
			if(dd.GetGlobalParamAsNumber("Application Settings/Problem/CopyIncidentCIs")==1)
			{
				var oResultCIs = app.cmdb.get_call_ci_info(oResults.selectedkeys , "INCIDENT", "");
				if(oResultCIs.keys!="")_swdoc.link_affect_cis_to_problem(oResultCIs.keys,oResultCIs.text);
			}
		}
		_swdoc.filter_impact_customers(_swdoc.EF(),true);
	}

	//-- NWJ 02.11.2006 - unlink incidents
	if(nItemID == iSearch)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
		
    	//-- NWJ 02.11.2006
    	//-- open call search form in select mode
   		var strURL = "canreset=1";
    	app.itsm.search_calls("INC",strURL,true,function(oResults)
    	{
    		if(oResults.selectedkeys=="")
    		{
    			return;
    		}
    		else
    		{
    			funcFinalProcessing(oResults);
    		}
    	});
	}
	else if(nItemID == iRelated)
	{
		var strCIKeys = app.g.sqllist_get_valuesdel(sl_configitems1, 'pk_auto_id', ",", "");
		var strServiceKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");
		if(strServiceKeys!="")
		{
			if(strCIKeys!="")strCIKeys+=",";
			strCIKeys+=strServiceKeys;
		}
  		var strURL = "canreset=1";
  		if(strCIKeys!="")
  		{
  			strURL += "&ci_keys="+strCIKeys;
  		}
    	app.itsm.search_calls("INC",strURL,true,function(oResults)
    	{
    		if(oResults.selectedkeys=="")
    		{
    			return;
    		}
    		else
    		{
    			funcFinalProcessing(oResults);
    		}
 
    	});
  	 	
	}
	else if(nItemID == iChildren)
	{
		var strChildKeys = "";
		var strCIKeys = app.g.sqllist_get_valuesdel(sl_configitems1, 'pk_auto_id', ",", "");
		var strServiceKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");
		if(strServiceKeys!="")
		{
			if(strCIKeys!="")strCIKeys+=",";
			strCIKeys+=strServiceKeys;
		}
		
		if(strCIKeys!="")
		{
			var strParams = "cis=" + pfu(strCIKeys);
			var aRS = app.g.get_sqrecordset("call/problem/get_impacted_child_cis",strParams);
			while(aRS.Fetch())
			{
				var childValue = app.g.get_field(aRS,"fk_child_id");
				if(strChildKeys!="")strChildKeys+=",";
				strChildKeys+=childValue;
			}
		}

 		var strURL = "canreset=1";
  		if(strChildKeys!="")
  		{
  			strURL += "&ci_keys="+strChildKeys;
  		}
    	app.itsm.search_calls("INC",strURL,true,function(oResults)
    	{
    		if(oResults.selectedkeys=="")
    		{
    			return;
    		}
    		else
    		{
    			funcFinalProcessing(oResults);
    		}
    	});
  	 	
	}
	else if(nItemID == iParents)
	{
		var strParentKeys = "";
		var strCIKeys = app.g.sqllist_get_valuesdel(sl_configitems1, 'pk_auto_id', ",", "");
		var strServiceKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");
		if(strServiceKeys!="")
		{
			if(strCIKeys!="")strCIKeys+=",";
			strCIKeys+=strServiceKeys;
		}
		
		if(strCIKeys!="")
		{
			var strParams = "cis=" + pfu(strCIKeys);
			var aRS = app.g.get_sqrecordset("call/problem/get_impacted_child_cis",strParams);
			while(aRS.Fetch())
			{
				var parentValue = app.g.get_field(aRS,"fk_parent_id");
				if(strParentKeys!="")strParentKeys+=",";
				strParentKeys+=parentValue;
			}
		}

  		var strURL = "canreset=1";
  		if(strParentKeys!="")
  		{
  			strURL += "&ci_keys="+strParentKeys;
  		}
    	app.itsm.search_calls("INC",strURL,true,function(oResults)
    	{
    		if(oResults.selectedkeys=="")
    		{
    			return;
    		}
    		else
    		{
    			funcFinalProcessing(oResults);
    		}
    	});
  	 	
	}
	else if(nItemID == iProfile)
	{
		if(itsm_opencall_problem.inc_profilecode=="")
		{
			MessageBox('The problem recognition profile has not been set.');
			return;
		}
 		var strURL = "canreset=1&probcode="+itsm_opencall_problem.inc_profilecode;
     	app.itsm.search_calls("INC",strURL,true,function(oResults)
     	{
     		if(oResults.selectedkeys=="")
     		{
     			return;
     		}
     		else
     		{
     			funcFinalProcessing(oResults);
     		}
     	});
	}
	else
	{
		funcFinalProcessing();
	}
	
}

