						mbtn_rfc_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	
	//-- NWJ - unlink rfc
	if(nItemID == iDisassociate)
	{
		if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rfccause,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'RFC-CPROBLEM'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This Problem has been dis-associated as being caused by the following Change Requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Change Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRFCText = "The Problem (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated as being caused by this Change Request";
					app.g.new_diaryevent(arrCallrefs[x], strRFCText,"Problem Management","Problem Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Problem Management","Change Request Dis-association");
				sl_rfccause.Refresh();
			}
		
			

		}	
	}

}

						btn_srch_rfc1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

   	var strURL = "canreset=1";    	
    app.itsm.search_calls("RFC",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_rfc = oResults.selectedkeys.split(",");  
		var array_classes = oResults.selectedclasses.split(",");
 		var strText = "This Problem has been associated as being caused by the following Change Requests\n\n";
		var boolRFCs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rfc.length;x++)
    	{
        	var rfc_callref = array_rfc[x];
        	var rfc_callref_text = app.g.pad_callref(rfc_callref);
			//-- check that rfc is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rfccause,rfc_callref,"callref");
			if (rowPos == -1)
			{ 
				boolRFCs = true;		
				//-- call g method to link problem to incident
				if(app.g.relate_calls(rfc_callref,_swdoc.OC().callref,'RFC-CPROBLEM'))
				{
					strText += "Change Request" + " (" + rfc_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRfcText = "The incident (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been caused by this Change Request";
					app.g.new_diaryevent(rfc_callref,strRfcText,"Problem Management","Problem Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolRFCs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Problem Management","Change Association");
			sl_rfccause.Refresh();
		}
    });
}

						sl_rfccause_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here
	var boolActive = (sl_rfccause.rowCount() > 0);
	var selCount = app.g.sl_getselected_count(sl_rfccause);	
	_een(mbtn_rfc_views1,(selCount==1));	
	_een(mbtn_rfc_actions1,boolActive);
	


}

