	}
}

						mbtn_rel_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	
	//-- NWJ - unlink release
	if(nItemID == iDisassociate)
	{
		if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rels1,0,",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs,_swdoc.OC().callref,'RELEASE-CPROBLEM'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This problem has been dis-associated as being caused by following release requests\n\n";
				
				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Release Request (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
					
					var strRELText = "The problem (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated as being caused by this release request";
					app.g.new_diaryevent(arrCallrefs[x], strRELText,"Problem Management","Problem Dis-association");
					
				}
				
				
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Problem Management","Release Dis-association");
			}
		
			sl_rels1.Refresh();
			sl_rels1_OnItemSelected(0);
		}	
	}

}

						btn_srch_rel1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	var strURL = "canreset=1";  

    app.itsm.search_calls("REL",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_rels = oResults.selectedkeys.split(",");  
		var strText = "This Problem has been associated as being caused by the following release requests\n\n";
		var boolRELs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rels.length;x++)
    	{
        	var rel_callref = array_rels[x];
        	var rel_callref_text = app.g.pad_callref(rel_callref);
			
			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rels1,rel_callref,0);
			if (rowPos == -1)
			{ 		
				boolRELs=true;
				//-- call g method to link problem to incident
				if(app.g.relate_calls(rel_callref,_swdoc.OC().callref,'RELEASE-CPROBLEM'))
				{
					strText += "Release Request (" + rel_callref_text + ") associated\n";
					
					//-- update rfc diary
					strRelText = "The release (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated as being caused by this problem.";
					app.g.new_diaryevent(rel_callref,strRelText,"Release Management","Problem Association");
				}
			}
    	} //-- eof for
	
		//-- create incident call diary update then reload list
		if (boolRELs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Release Management","Problem Association");
			sl_rels1.Refresh();
		}
    });
}

						TabChanges_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	if(nTab==0)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
