						mbtn_inc_actions3_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;
	
	//-- NWJ - unlink incident
	if(nItemID == iDisassociate)
	{
		
		var strText = "This Problem has been dis-associated with the following KB Documents\n\n";
		var strDocs = app.g.sl_getselected_valuesdel(sl_kbs1,0,",");
		if(strDocs!= "")
		{
			app.oKB.unrelate_calls_from_kb(strDocs,_swdoc.OC().callref);
			var strDocRefs = app.oKB.get_call_kbs(_swdoc.OC().callref);
			sl_kbs1.Refresh();
			strText += "Knowledgebase Document (" + strDocs + ") dis-associated\n";
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Problem Management","Knowledgebase Document Dis-association");
		}	
	}	
}

						btn_search_inc3_OnPressed()
{
    var funcRefreshAndReturn = function()
    {
    	sl_kbs1.Refresh();
		sl_kbs1.SetRowSelected(0);
		sl_kbs1_OnItemSelected(0);
		return;
    }
    app.g.search_for("swkb", true,"",function(oRes)
    {
    	var strKeys = oRes.selectedkeys;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
 		var strText = "This Problem has been associated with the following Knowledgebase Documents\n\n";
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_kbs1,fk_key,"docref")==-1)
			{
				app.oKB.relate_call_to_kb(fk_key,_swdoc.OC().callref,'Used');
				strText += "Knowledgebase Document (" + fk_key + ") associated\n";
			}
		}			
		app.g.new_diaryevent(_swdoc.OC().callref ,strText,"Problem Management","Knowledgebase Document Association");
		
		//ES - F0111784 - Add KB _swdoc solution to workaround tab
     	if (arrKeys.length<=1)
     	{
          	var dbValue = new SqlQuery;
          	//get solution value for selected KB _swdoc
          	dbValue.InvokeStoredQuery("call/problem/get_kb_solution","docref_in="+arrKeys);
          	if(dbValue.Fetch())
          	{
               	var strSolution = app.g.get_field(dbValue,"solution");
               	//if solution is not blank, then prompt
               	if (strSolution!="")
               	{
                    	MessageBox("Would you like to copy the Solution of this KnowledgeBase document to the Workaround tab?",MB_YESNO|MB_ICONQUESTION,function(promptMsg)
                    	{
                    		if (promptMsg==6)
                    		{
                         		//if workaround text is blank, then set
                         		if (_swdoc.PRB().wa_info=="")
                         		{
                              		currentText = "KnowledgeBase Document (" + arrKeys + ") Solution: ";
                              		currentText += "\r\n";
                              		_swdoc.PRB().wa_info = currentText + strSolution;
                              		_swdoc.UpdateFormFromData();
                              		_een(_swdoc.MF().btn_convke,true);
                              		funcRefreshAndReturn();
                         		}
                         		//if workaround text is not blank, then add solution to current workaround text
                         		else
                         		{
                              		var currentText = _swdoc.PRB().wa_info;
                              		currentText += "\r\n";
                              		currentText += "\r\n";
                              		currentText += "KnowledgeBase Document (" + arrKeys + ") Solution: ";
                              		currentText += "\r\n";
                              		_swdoc.PRB().wa_info = currentText + strSolution;
                              		_swdoc.UpdateFormFromData();
                              		funcRefreshAndReturn();
                         		}
                    		}
                    		else
                    		{
                    			funcRefreshAndReturn();
                    		}
                    	});
                    	
               	}
               	else
               	{
               		funcRefreshAndReturn();
               	}
          	}
          	else
          	{
          		funcRefreshAndReturn();
          	}
     	} //End - F0111784
     	else
     	{
     		funcRefreshAndReturn();
     	}
    });
}

