//-- setup form state depending on form mode
function setup_form_state()
{

	//-- nwj - 16.05.2013 - make sure control is set to visible as designer auto hides control if all tab items are hidden
	_evi(_swdoc.MF().TabStage,true);


	var boolUpdate = (_swdoc.STAGE().fk_workflow_id!="");
	if(boolUpdate)
	{
		//-- updating

		//-- Enable/Disable Auth Settings 88608
		if(_swdoc.STAGE().flg_authorisation==1)
		{
			_een(_swdoc.MF().TabAuthCG, true);
			_een(mainform.lb_template1, true);
			_een(mainform.lb_template2, true);
			_een(mainform.lb_mailbox1, true);
			_een(mainform.btn_del_auth, true);
			_een(mainform.sl_authors, true);
		}
		else
		{
			_een(_swdoc.MF().TabAuthCG, false);
			_een(mainform.lb_template1, false);
			_een(mainform.lb_template2, false);
			_een(mainform.lb_mailbox1, false);
			_een(mainform.btn_del_auth, false);
			_een(mainform.sl_authors, false);
		}
	}
	else
	{
		var strWorkflowID = _swdoc.GetArg("fk_workflow_id");
		if(strWorkflowID!="")
		{
			//-- we are inserting a new stage
			_swdoc.STAGE().fk_workflow_id = strWorkflowID;
		}

		var strSEQ = _swdoc.GetArg("seq");
		if(strSEQ!="")_swdoc.STAGE().seq = strSEQ;
	}

	//-- nwj - 16.05.2013 - if opened from bpmui then only show the tab items it as specified in bpmui/js/actions.js hsl: links
	var frombpmui = _swdoc.GetArg("bpmui");	
	if(frombpmui=="1")
	{
		var strShowTabs = _swdoc.GetArg("showtabs");
		var arrTabs = strShowTabs.split(",");
		for(var x=0;x<arrTabs.length;x++)
		{
			_swdoc.MF().TabStage.ShowTabItem(arrTabs[x],true);
		}	
		_etab(_swdoc.MF().TabStage,arrTabs[0]);
	}
	else
	{
		//-- hide stage actions and tasks if we are inserting / show if updating
		_swdoc.MF().TabStage.ShowTabItem(0,true);	
		_swdoc.MF().TabStage.ShowTabItem(1,boolUpdate);
		_swdoc.MF().TabStage.ShowTabItem(2,boolUpdate);	
		_swdoc.MF().TabStage.ShowTabItem(3,boolUpdate);
		_swdoc.MF().TabStage.ShowTabItem(4,boolUpdate);
		_swdoc.MF().TabStage.ShowTabItem(5,boolUpdate);	
		_swdoc.MF().TabStage.ShowTabItem(6,boolUpdate);	
		_etab(_swdoc.MF().TabStage,0);
	}
	//-- set form modified to false as users complain about closing forms and getting prompted to save / cancel when they
	//-- havent done anything.

	//###20120712
	if (boolUpdate){
		var e_template = _swdoc.STAGE().notif_template;
		if(""!=_swdoc.STAGE().notif_mailbox){
			_swdoc.MF().lb_notif_mailbox_OnValueChanged(_swdoc.STAGE().notif_mailbox);
			_swdoc.STAGE().notif_template = e_template;
		}
		_swdoc.UpdateFormFromData();
		_swdoc.MF().fld_notif_days_OnValueChanged(_swdoc.STAGE().notif_days);
	}

	_swdoc.ResetModiedFlag("bpm_stage");
}

