btn_tdown_OnPressed()
{
	//-- NWJ : move stage seq down 
	//-- get selected task id to move down
	var currRow = sl_tasks.curSel;
	var intDownTaskID = sl_tasks.GetItemTextRaw(sl_tasks.curSel, 'pk_auto_id');
	var intDownTaskSeq = sl_tasks.GetItemTextRaw(sl_tasks.curSel, 'seq');	

	//-- get stage id to move up
	var intUpTaskID = sl_tasks.GetItemTextRaw(sl_tasks.curSel+1, 'pk_auto_id');
	var intUpTaskSeq = sl_tasks.GetItemTextRaw(sl_tasks.curSel+1, 'seq');

	//-- update seq id for both stages
	var strParams = "tid="+pfu(intUpTaskID);
	if(app.g.submitsqs("form/bpm_stage/dec_task_seq", strParams))
	{
		var strParams = "tid="+pfu(intDownTaskID);
		if(app.g.submitsqs("form/bpm_stage/inc_task_seq", strParams))
		{
			//-- refresh list
			sl_tasks.Refresh();
			sl_tasks.SetRowSelected(currRow+1, true, false);
		}
	}
}

btn_tup_OnPressed()
{
	//-- NWJ : move stage seq up 
	//-- get selected task id to move up
	var currRow = sl_tasks.curSel;
	var intUpTaskID = sl_tasks.GetItemTextRaw(sl_tasks.curSel, 'pk_auto_id');
	var intUpTaskSeq = sl_tasks.GetItemTextRaw(sl_tasks.curSel, 'seq');	

	//-- get stage id to move down
	var intDownTaskID = sl_tasks.GetItemTextRaw(sl_tasks.curSel-1, 'pk_auto_id');
	var intDownTaskSeq = sl_tasks.GetItemTextRaw(sl_tasks.curSel-1, 'seq');

	//-- update seq id for both stages
	var strParams = "tid="+pfu(intUpTaskID);
	if(app.g.submitsqs("form/bpm_stage/dec_task_seq", strParams))
	{
		var strParams = "tid="+pfu(intDownTaskID);
		if(app.g.submitsqs("form/bpm_stage/inc_task_seq", strParams))
		{
			//-- refresh list
			sl_tasks.Refresh();
			sl_tasks.SetRowSelected(currRow-1, true, false);
		}
	}
}

