	var intCondID = sl_conditions.GetItemTextRaw(sl_conditions.curSel, 'pk_auto_id');
	var delSequence = sl_conditions.GetItemTextRaw(sl_conditions.curSel, "seq");
	if( (intCondID!="") && (confirm(strConfirm)) )
	{
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("in_condid", intCondID);

		// Invoke the method
		if(xmlmc.Invoke("VPME", "bpmDeleteStageCond"))
		{

		}
		else
		{
			MessageBox(xmlmc.GetLastError());
		}	

		sl_conditions.Refresh();

		//-- loop through list and set seq for each to #
		var intTask=0;
		for(var x=0; x < sl_conditions.rowCount();x++)
		{
			var thisSeq = sl_conditions.GetItemTextRaw(x, "seq");
