//-- init form fields
function setup_form_fields(boolInsert)
{
	_swdoc.boolInsertingType = boolInsert;

	_een(_swdoc.MF().tb_code, boolInsert); //-- enable code change when inserting
	_een(_swdoc.MF().btn_add_ci2, (!boolInsert));
	_een(_swdoc.MF().btn_new_status1, (!boolInsert));	
	_een(_swdoc.MF().btn_new_status, (!boolInsert));
	_een(_swdoc.MF().btn_new_reltype, (!boolInsert));	
	_een(_swdoc.MF().btn_new_att, (!boolInsert));
	_een(_swdoc.MF().mbtn_ola_actions3, (!boolInsert));
	
	//-- Merope - FR6.1.3, remove service request definitions
	//MF().tabType.ShowTabItem(3,!boolInsert);
	_swdoc.MF().tabType.ShowTabItem(3,_swdoc.oType().flg_comps==1);
	_etab(_swdoc.MF().tabType, 0);
	
	_swdoc.orig_cost = _swdoc.oType().bus_cost;
	_swdoc.orig_price = _swdoc.oType().bus_price;
	_etab(_swdoc.MF().tabother, 0);
	
	_swdoc.strConfigType = _swdoc.oType().pk_config_type;
	_swdoc.strDisplayType = _swdoc.oType().type_display;
	
}

						OnDataSaved()
{
	//-- saved data no longer inserting - setup fields for update
	if (_swdoc.boolInsertingType) 
	{
		app.cmdb.copy_parenttype_statuslevels(_swdoc.oType().pk_config_type, _swdoc.oType().fk_config_type);
		app.cmdb.copy_parenttype_relationtypes(_swdoc.oType().pk_config_type, _swdoc.oType().fk_config_type);		
		_swdoc.MF().sl_status.Refresh();
		_swdoc.MF().sl_reltypes.Refresh();
	}
	
	_swdoc.setup_form_fields(false);
}

						OnFormClosing()
{
	// TODO: Add your event handler code here
   var pDoc = app.g.get_me_document("CMDB_ENTITY",1);
    if(pDoc)
    {
    	var intCurSel = pDoc.mainform.sl_types.curSel;
    	pDoc.mainform.sl_types.Refresh();
    	pDoc.mainform.sl_types.SetRowSelected(intCurSel,true);
    }


	return true;
}

							OnFormLoaded()
{
	//-- set defaults
	var boolInsert = (_swdoc.oType().pk_config_type=="");
	if(boolInsert)
	{
		//-- set fields from args, update doc and reste modified flag
		app.g.popuplate_form_fromargs("config_typei", _swdoc, true, true);
	}
	
	//-- hide / show tab items depending on mode
	_swdoc.setup_form_fields(boolInsert);
	
	//Get CI Data Form and Extension Table CI-TYPE simpleList
	var strID = "CI-TYPE";
	var arrType = new Array();
	var arrSplit  = new Array();
	_swdoc.strForm  = "";
	var strType = "";
	var p = {};
	p.id = strID;
	var oRS = app.g.get_sqrecordset("GENERAL/get.simple_list", p);
	var intCount=0;

	while(oRS.Fetch())
	{
		arrType[intCount] = app.g.get_field(oRS,"value");
		intCount++;
	}
	for (var i = 0; i < arrType.length; i++)
	{
		strType = arrType[i];
		arrSplit = strType.split(",");
		_swdoc.strForm += arrSplit[0];
		_swdoc.strForm += "|";
	}
	//Set Field1 picklist
	mainform.Field1.pickList = _swdoc.strForm;
	_swdoc.MF().tabother_OnTabSelected(0);

}

