							btn_add_ci2_OnPressed()
{
	//-- Prompt analyst for number of clones
	var strText = "Number of Configuration Items of type "+config_typei.pk_config_type+"?";
	var intNumberOfCopies = prompt(strText,"1");
	if(intNumberOfCopies==null)
	{
		return;
	}

	var funcRefresh = function()
	{
		sl_items.Refresh();
		tabType_OnTabSelected(2);
	}
	
	var funcCreateCIs = function()
	{
		var resultValue = app.cmdb.create_ci(config_typei.pk_config_type,intNumberOfCopies);
		if(resultValue<0)
		{
			MessageBox("Failed to clone CI.");
			funcRefresh();
		}
		else
		{
			var boolConfirm = confirm("Creation completed. View new configuration items?");
			if(boolConfirm)
			{
				app.cmdb.search_ci(true, "canreset=1&isauthorised=&isactivebaseline=&ck_config_item=New "+config_typei.pk_config_type+"%",function()
				{
					funcRefresh();
				});
			}
			else
			{
				funcRefresh();
			}
		}
	}
	
	if(isNaN(intNumberOfCopies))
	{
		MessageBox("Invalid number of items. Please input a number.");
		funcRefresh();
	}
	else
	{
		if(intNumberOfCopies>0)
		{
			if(intNumberOfCopies>100)
			{
				if(!confirm("You have entered a large number of items to be created, proceed?"))
				{
					return false;
				}
				else
				{
					// -- Create CIs
					funcCreateCIs();
				}
			}
			else
			{
				// -- Create CIs
				funcCreateCIs();
			}
		}
		else
		{
			MessageBox("Please input a value greater than 0.");
			funcRefresh();
		}
	}
}

