							mbtn_ola_actions3_OnMenuItem(strName, nItemID)
{
	var iAssignKB = 1;
	var iRemoveKB = 2;	

	
	if(nItemID == iAssignKB)
	{
        app.g.search_for("swkb", true, "", function(oRes)
        {
        	var strKeys = oRes.selectedkeys;
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
	
			var boolAssoc = confirm("Would you like to associate the KB Documents with the existing CI(s) of this type?");
	
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_kb2,fk_key,"docref")==-1)
				{
					app.oKB.relate_ci_type_to_kb(_swdoc.oType().pk_config_type,_swdoc.oType().type_display,fk_key);
					app.g.sqllist_appendfilter(sl_kb2, fk_key, "docref");	
					
					if(boolAssoc)
					{
						var xmlmc = new XmlMethodCall;
						xmlmc.SetValue("docref", fk_key);
						xmlmc.SetValue("configType", _swdoc.oType().pk_config_type);
						
						//-- Invoke the method
						if(xmlmc.Invoke("VPME", "relateKBtoCIs"))
						{
		
						}
						else
						{
							MessageBox(xmlmc.GetLastError());
						}	
					}
				}
			}
			
			sl_kb2.Refresh();
			sl_kb2.SetRowSelected(0);
			sl_kb2_OnItemSelected(0);
			return;
        });
		
    }
	else if(nItemID == iRemoveKB)
	{
		var strConfirm = "Are you sure you want to remove the selected Knowledgebase article from the Item Definition?";
		if(confirm(strConfirm))
		{
			var fk_kb_id = app.g.sl_getprikeyvalue(sl_kb2, sl_kb2.curSel);
			app.oKB.unrelate_ci_type_from_kb(fk_kb_id,_swdoc.oType().pk_config_type);	
		}
		var strDocs = app.oKB.get_ci_types_kbs(_swdoc.oType().pk_config_type);
		if(strDocs=="")strDocs="-1";
		_slf(sl_kb2,"swc=docref&kvs="+strDocs);
		_swdoc.MF().sl_kb2.Refresh();
	}
	




}

