							Field1_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

		//Get CI Types Form and Tables from CI-TYPE List
	var strID = "CI-TYPE";
	var arrType = new Array();
	var arrSplit  = new Array();
	_swdoc.strTable  = "";
	var strType = "";
	var p = {};
	p.id = strID;
	p.where = strValue;
	var oRS = app.g.get_sqrecordset("GENERAL/get.simple_list", p);
	var intCount=0;

	while(oRS.Fetch())
	{
		
		arrType[intCount] = app.g.get_field(oRS,"value");
		intCount++;
	}
	for (var i = 0; i < arrType.length; i++)
	{
		strType = arrType[i];
		arrSplit = strType.split(",");
		_swdoc.strTable = arrSplit[1];
	}
	
	var strDocTable = _swdoc.strTable;
	//Set Form Pick List
	_eva(_swdoc.MF().lb_tables , strDocTable);
	_eva(_swdoc.oType().extended_table , strDocTable);
	_swdoc.oType().extended_table = strDocTable;

}

