//-- F0105434
// RJC as part of 105434 this code has been refactored to use xmlmc
function update_default_bpm(strCallclass,strWorkflow)
{
	var strParams = "pk="+pfu(strCallclass);
	var oRec = app.g.get_sqrecordset("general/get_sys_set_defbpm",strParams);

	if(strWorkflow=="")
	{
		if(oRec.Fetch())
		{
			//delete row
			var strId = app.g.get_field(oRec, "pk_id");
			app.g.xmlmcexecute_delete('sys_sett_defbpm', strId);
		}
	}
	else
	{
		if(oRec.Fetch())
		{
			var strId = app.g.get_field(oRec, "pk_id");
			//update row
			var oUpdateTmp = new oXmlmcUpdate('sys_sett_defbpm');
			oUpdateTmp.setfield('pk_id',strId);
			oUpdateTmp.setfield('pk_callclass',strCallclass);
			oUpdateTmp.setfield('fk_def_bpm',strWorkflow);
			oUpdateTmp.execute();
		}
		else
		{
			//insert row
			var oInsertTmp = new oXmlmcInsert('sys_sett_defbpm');
			oInsertTmp.setfield('pk_callclass',strCallclass);
			oInsertTmp.setfield('fk_def_bpm',strWorkflow);
			oInsertTmp.setfield('appcode',app.itsm.get_session_param("dataset"));
			oInsertTmp.execute();
		}
	}
}

function load_default_process(oEle, strCallclass)
{
	
	var strParams = "pk="+pfu(strCallclass);
	var oRec = app.g.get_sqrecordset("general/get_sys_set_defbpm",strParams);
	if(oRec.Fetch())
	{
		var strValue = app.g.get_field(oRec, "fk_def_bpm");
		_eva(oEle,strValue);
	}
}

