							OnFormLoaded(bDataReloaded)
{
	if(!app.session.HaveAppRight(PG_BPM, BPM_CANMANAGE, app.session.dataDictionary)) 
	{
		MessageBox("You do not have permission to manage business processes.");
		_swdoc.CloseForm();
		return false;
	}
	
	// -- ES - BPM Utility is not available in the Web Client
	if(app.bWebClient)
	{
		_evi(_swdoc.MF().btn_open_bpmutil , false);
		_een(_swdoc.MF().btn_open_bpmutil,false);
	}
	else
	{
		if(_swdoc.MF().btn_open_bpmutil.visible)
		{
			var boolSetting = oSettings.get_setting("boolBPMGPDIntegrationEnabled");
			if(boolSetting)
			{
				_een(_swdoc.MF().btn_open_bpmutil,true);
			}
		}
	}
	
    app.g.add_me_document("ITSM_PROCESSES", 1, _swdoc);
	_evi(mainform.btn_licensed_approvers , (app.session.isAdmin));
	TabSettings_OnTabSelected(TabSettings.tab);
}

