							TabSettings_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	//-- RJC F0105434
	
	var tChange = 0;
	var tRel = 1;
	var tSer = 2;
	var tInc = 3;
	var tProb = 4;
	var tCond = 5;
	
	
	if(nTab==0)
	{
		TabChange_OnTabSelected(0);
	}
	else if(nTab==1)
	{
		_swdoc.load_default_process(mainform.fld_rp,"Release Request");
		_swdoc.MF().sl_rprocess.Refresh();
	}
	else if(nTab==2)
	{
		_swdoc.load_default_process(mainform.fld_sp,"Service Request");
		_swdoc.MF().sl_srprocess.Refresh();
	}
	else if(nTab==3)
	{
		_swdoc.load_default_process(mainform.fld_ip,"Incident");
		tInc_OnTabSelected(0);
	}
	else if(nTab==4)
	{
		_swdoc.load_default_process(mainform.fld_pp,"Problem");
		_swdoc.MF().sl_srprocess2.Refresh();
	}else if(nTab == tCond)
	{
		_swdoc.MF().sl_conditions.Refresh()
	}
	//-- EOF F0105434

}

