							btn_del_cond_OnPressed()
{
	var condId = sl_conditions.GetItemTextRaw(sl_conditions.curSel, 'pk_cond_id');
	var strParams = "condid="+condId;
	var intCount = app.g.sqs_rowcount("count/cond_proc_assoc",strParams);

  	if (intCount>0)        
   	{
		//--
		//-- bug fix 71859
		if(intCount==1)
			var strConfirm = "There is currently " + intCount + " process which uses this condition. Deleting this condition will remove this association. Are you sure you want to delete the selected condition?";
   		else
   			var strConfirm = "There are currently " + intCount + " processes which use this condition. Deleting this condition will remove these associations. Are you sure you want to delete the selected condition?";
   			
   		var strCondId = sl_conditions.GetItemTextRaw(sl_conditions.curSel, 'pk_cond_id');
   		if( (strCondId!="") && (confirm(strConfirm)) )
		{
		
			app.g.xmlmcexecute_delete("bpm_authcond",strCondId,true);
				
			var xmlmc = new XmlMethodCall;
			xmlmc.SetValue("condId", condId);
			if(xmlmc.Invoke("VPME", "bpmDeleteConditionRels"))
			{
	
			}
			else
			{
				MessageBox(xmlmc.GetLastError());
			}	
			
			_swdoc.MF().sl_conditions.Refresh();
			_swdoc.MF().sl_conditions.SetRowSelected(0);
   		}	
   		return;   
   	}
   	else
   	{
   		var strConfirm = "Are you sure you want to delete the selected condition?";	
   		var strCondId = sl_conditions.GetItemTextRaw(sl_conditions.curSel, 'pk_cond_id');
   		if( (strCondId!="") && (confirm(strConfirm)) )
		{
		
			app.g.xmlmcexecute_delete("bpm_authcond",strCondId,true);
				
			var xmlmc = new XmlMethodCall;
			xmlmc.SetValue("condId", condId);
			if(xmlmc.Invoke("VPME", "bpmDeleteConditionRels"))
			{
	
			}
			else
			{
				MessageBox(xmlmc.GetLastError());
			}	
			
			_swdoc.MF().sl_conditions.Refresh();
			_swdoc.MF().sl_conditions.SetRowSelected(0);
   		}	
   	}
	

}

