						var origSubCost = "";
var origReqCost = "";
function targ()
{
	return _swdoc.sc_target;
}

function MF()
{
	return mainform;
}

function setup_form_fields()
{
	if(sc_target.pk_auto_id<1)
	{
		var strService = _swdoc.GetArg("fk_auto_id");
		sc_target.fk_auto_id = strService;
		sc_target.created_by = app.session.analystId;
		_swdoc.ResetModiedFlag("sc_target");
	}else
	{
		_swdoc.origSubCost = sc_target.ave_subsc_cost;
		_swdoc.origReqCost = sc_target.ave_request_cost;
	}
}

							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here

	_swdoc.setup_form_fields();
}

							OnFieldValueChanged(strName, strValue)
{
	// TODO: Add your event handler code here
	mainform.SetTimer(1, 50);
	
}

							OnTimer(nTimer)
{
	// TODO: Add your event handler code here

	if(nTimer==1)
	{
		mainform.KillTimer(nTimer);
		var x = _swdoc.targ().jan_r + _swdoc.targ().feb_r + _swdoc.targ().mar_r + _swdoc.targ().apr_r + _swdoc.targ().may_r + _swdoc.targ().jun_r + _swdoc.targ().jul_r + _swdoc.targ().aug_r;
		var x = x + _swdoc.targ().sep_r + _swdoc.targ().oct_r + _swdoc.targ().nov_r + _swdoc.targ().dec_r;
		_swdoc.targ().request_target = x;
		
		var y = _swdoc.targ().jan_s + _swdoc.targ().feb_s + _swdoc.targ().mar_s + _swdoc.targ().apr_s + _swdoc.targ().may_s + _swdoc.targ().jun_s + _swdoc.targ().jul_s + _swdoc.targ().aug_s;
		var y = y + _swdoc.targ().sep_s + _swdoc.targ().oct_s + _swdoc.targ().nov_s + _swdoc.targ().dec_s;
		_swdoc.targ().subsc_target = y;
		_swdoc.UpdateFormFromData();
	}
	
}

							Field27_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

	if(isNaN(strValue))
	{
		sc_target.ave_request_cost = _swdoc.origReqCost;
	}
	else
	{
		sc_target.ave_request_cost = format_float_to_decimal_str(strValue);
		_swdoc.origReqCost = sc_target.ave_request_cost;
	}
	_swdoc.UpdateFormFromData();
}

