//-- NWJ - 11.08.2008 - read in url and find any matching bindings - if found set field value and disable
//-- as means we have passed in fixed search criteria - also returns fixed filter
function setup_fixed_criteria()
{
	var boolCanChange = (_swdoc.GetArg('canreset')=="1");
	var strFilter = "";
	for(var x = 0; x < _swdoc.MF().elements.length;x++)
	{
		var element = _swdoc.MF().elements[x];
		if((element.dataRef!="") && ((element.type == FE_FIELD)||(element.type == FE_SEARCHFIELD)))
		{
			var arrInfo = element.dataRef.split(".");
			var strFixedValue = unescape(_swdoc.GetArg(arrInfo[1]));
			if(strFixedValue!="")
			{
				_ete(element, strFixedValue);
				_een(element, boolCanChange);

				var colValue = app.g.encapsulate(arrInfo[0],arrInfo[1],strFixedValue);

				if(strFilter!="")strFilter += "&";
				strFilter += element.dataRef + "=" + colValue;

			}

			//-- F0098488
			//-- If appcode filter is set then apply it to the organisation 
			if((arrInfo[1]=="fk_company_id") && (dd.GetGlobalParamAsNumber("Application Settings/UseAppCodeFilter")==1))
				_slf(element , "_swc_appcode IN( "+app.itsm.get_session_param("datasetFilterList")+" )");
		}

	}
	return strFilter;
}

btn_search_OnPressed()
{
	//-- 19.01.2006 - NWJ
	//-- create search criteria

	_een(btn_select, false);
	app.g.sqllist_clear(sl_calls);

	var boolAND = (lb_type.value==1);
	var strFilter = app.g.create_search_params(mainform,"opencall","",boolAND,false);
	var strCalls = "";

	if(sl_configitems1.rowCount()>0)
	{
		var strKeys = app.g.sqllist_get_valuesdel(sl_configitems1, 'pk_auto_id', ",", "");	
		var strParams = "keys="+pfu(strKeys);
		var oRS = app.g.get_sqrecordset("cmdb/select_calls_related_to_cis",strParams);
		while(oRS.Fetch())
		{
			var currKey = app.g.get_field(oRS,"FK_CALLREF");
			if (strCalls!="")strCalls +=",";
			strCalls += app.g.pfs(currKey);
		}
	}

	if(sl_services.rowCount()>0)
	{
		var strKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");	
		var strParams = "keys="+pfu(strKeys);
		var oRS = app.g.get_sqrecordset("cmdb/select_calls_related_to_cis",strParams);
		while(oRS.Fetch())
		{
			var currKey = app.g.get_field(oRS,"FK_CALLREF");
			if (strCalls!="")strCalls +=",";
			strCalls += app.g.pfs(currKey);
		}
	}

	if(strCalls!="")
	{
		if(strFilter!="")
			strFilter += "&";
		strFilter += "cr="+strCalls;
	}

	//-- check if user selected logged after or before dates
	if(tb_lda.value>"")
	{
		if (strFilter != "") strFilter = strFilter+"&";			
		strFilter += "lda="+pfu(tb_lda.value);
	}

	if(tb_ldb.value>"")
	{
		if (strFilter != "") strFilter = strFilter+"&";			
		strFilter += "ldb="+pfu(tb_ldb.value);
	}	

	if (strFilter != "") strFilter = strFilter+"&";
	strFilter += _swdoc.callclass;		

	//-- apply quick filter
	switch(lb_qcall.value)
	{
		case "1":
			//-- we want to filter active calls 
			if (strFilter != "") strFilter = strFilter+"&";			
			strFilter += "bopen=1";
			break;
		case "2":
			//-- we want to filter resolved / closed calls
			if (strFilter != "") strFilter =  strFilter+"&";				
			strFilter += "bopen=0";
			break;
	}

	//-- apply filter
	app.g.sl_filter(sl_calls,strFilter,null,0);	
	_swdoc.MF().sl_calls.Refresh();
}

btn_clear_OnPressed()
{
	//-- clear search fields
	_eva(tb_lda, "");
	_eva(tb_ldb, "");
	_eva(lb_qcall, "");
	app.g.clear_form_fields("opencall",mainform,"",false);

	_swdoc.setup_fixed_criteria();

}

OnFormLoaded()
{
	//-- set field states
	_een(btn_select, false);
	_een(btn_subset, false);
	_een(btn_subselect, false);
	_een(btn_remsub, false);
	_een(btn_subclear, false);

	var strCIKeys = _swdoc.GetArg("ci_keys");
	if(strCIKeys!="")
	{
	 	app.g.sl_appendfilter(sl_configitems1, strCIKeys,"pk_auto_id");
 	 	app.g.sl_appendfilter(sl_services, strCIKeys,"pk_auto_id");

 	 	_slf(sl_services, sl_services.filter+"&sf=cis_servicesonly");
 	 	_slf(sl_configitems1, sl_configitems1.filter+"&sf=cis_excludeservice");
	}

	//-- get the search type (INC, PRB , RFC, REL)

	_swdoc.callclass = "callclass=ALL";
	var strTitle = "Search All Requests";
	var strSearchType = _swdoc.GetArg("type");
	switch (strSearchType)
	{
		case "MAJINC":
			strTitle = "Search For Major Incidents";		
			_swdoc.callclass = "callclass=INC";
			_ete(mainform.tb_majinc, "1");
			break;
		case "INC":
			strTitle = "Search For Incidents";		
			_swdoc.callclass = "callclass=INC";
			break;
		case "PRBONLY":
			strTitle = "Search For Problems";		
			_swdoc.callclass = "callclass=PRB";
			break;			
		case "PRB":
			strTitle = "Search For Problems and Known Errors";		
			_swdoc.callclass = "callclass=PRBKE";
			break;
		case "KE":
			strTitle = "Search For Known Errors";		
			_swdoc.callclass = "callclass=KE";
			break;		
		case "RFC":
			strTitle = "Search For Change Requests";		
			_swdoc.callclass = "callclass=RFC";
			break;
		case "REL":
			strTitle = "Search For Release Requests";				
			_swdoc.callclass = "callclass=REL";
			break;
		case "SR":
			strTitle = "Search For Service Requests";				
			_swdoc.callclass = "callclass=REQ";
			break;
		case "CPR":
			strTitle = "Search For Change Proposals";				
			_swdoc.callclass = "callclass=CPR";
			break;
	}

	_swdoc.SetTitle(strTitle);

	_swdoc.setup_fixed_criteria();

}

