//--
//-- based on the current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032
	
	//-- NWJ
	//-- assign _swdoc to app.cfg array (so we can refresh tasks list from task forms
	app.cfg.allocaterfcdoc(_swdoc.OC().callref,_swdoc)
		
	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}
	
	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;

	//-- if user can add tasks and this stage allows it
	var strParams = "cr="+app.pfu(_swdoc.OC().callref)+"&sid="+_swdoc.oStage().pk_stage_id;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);

    var boolHasTasks = intTaskCount>0;
    var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));

	app._een(_swdoc.MF().mbtn_mod_task,boolAddTasks);
	_swdoc.MF().TabDet.ShowTabItem(2,boolShowTasks);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}
	
	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDAUTH,app.session.dataDictionary)) )
		
	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(1, boolRequiresAuth||boolAddAuths);

	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	app._een(_swdoc.MF().lb_wf_state,(!(boolTaskStop|| boolRequiresAuth)));
	app._evi(_swdoc.MF().mbtn_mod_auth,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF
	
	//-- if we have data form show button
	app._een(_swdoc.MF().btn_bpm_form,((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));
	
	//-- select tab 0 or 1 depending on auth
	app._etab(_swdoc.MF().TabDet,(boolRequiresAuth)?1:0);
	// DTH: webclient refresh the Auth List if the Auth tab is displayed
	if (boolRequiresAuth)
	{
		_swdoc.MF().TabDet_OnTabSelected(1);
	}
	 
	 
	//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
	app._slf(_swdoc.MF().sl_components,"cr="+_swdoc.OC().callref);
	_swdoc.MF().sl_components.Refresh();
		
	//-- whether we enable the progress button, only do so if there is a progress associated with the bpm process
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		var intCount = app.g.sqs_rowcount("call/common/cnt_progress_items","wf="+app.pfu(_swdoc.OC().bpm_workflow_id));
		if(intCount>0)
			app._een(_swdoc.MF().btn_bpm_progress,true);
	}
	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);
	return true;
}

//-- given an analyst object (from app.global.js) load values for either monitored / raised by analyst
//-- for the rfc
function set_rfc_analyst(oAnalyst,boolMonitoredBy)
{
	if(!oAnalyst) return false;
	
	if(boolMonitoredBy)
	{
		_swdoc.RFC_EXT().monitored_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().monitored_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().monitored_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().monitored_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().monitored_by_email = oAnalyst.email;	
	}
	else
	{
		_swdoc.RFC_EXT().raised_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().raised_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().raised_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().raised_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().raised_by_email = oAnalyst.email;
	}
	_swdoc.UpdateFormFromData();	
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_cis_to_change(strKeys, strCIText)
{

	if(strKeys=="") return;
	//-- loop through selected CIs
	var strText = "";
	var arrayCIkeys = strKeys.split(",");
	var arrayCItext = strCIText.split(",");
	
	// -- SG, 02/07/2012, F0103324, required variable and array creation
	var itemType = "Configuration Item";
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();	
	
	for (var x=0;x<arrayCIkeys.length;x++)
    {
        var pk_ci_id = arrayCIkeys[x];
        var ci_item_text = arrayCItext[x];
		//-- check that CI is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_configitems1,pk_ci_id,'pk_auto_id');
		if (rowPos == -1)
		{ 
			
			// -- SG, 02/07/2012, F0103324
			// Build array of CI's that have the Bespoke Terms field populated
			var rec = app.g.get_record("config_itemi", pk_ci_id);
			if(rec.recordresolvedmsg != '')
			{
    			bespokeTermsArray[countBespokeTerms] = new Array(2);
    			bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    			bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    			countBespokeTerms++;
			}
			// End of array building
			
			//-- Relate the CI to the call
			if (app.cmdb.relate_ci_to_call(_swdoc.OC().callref,pk_ci_id,'REQUEST'))
			{
				if(strText != "") strText +=",";
				strText += ci_item_text;
			}
		}
	}   

	//-- apply call diary update for added CIs (if we have any to add)
	if (strText!="")
	{
		strText = "The following configuration items were added to this service request.\n" + strText;
		var strActionSource = "Service Request Management";
		var strCode			= "Linking Configuration Items";
		
		app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
		_swdoc.EF().sl_configitems1.Refresh();
	}
	
	// -- SG, 02/07/2012, F0103324
	app.bespokeTermsOutput(bespokeTermsArray, arrayCIkeys.length, "Configuration Item");
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function link_incidents_to_change(strSelectedIncs)
{
	var array_incs = strSelectedIncs.split(",");  
	var strText = "This service request has been associated with the following incidents\n\n";
	var boolINCs = false;
	//-- loop through selected problems and link them
    for (var x=0;x<array_incs.length;x++)
    {
        var inc_callref = array_incs[x];
        var inc_callref_text = app.g.pad_callref(inc_callref);
		
		//-- check that incident is not already in the list
		rowPos = app.g.sqllist_findrow_byvalue(_swdoc.EF().sl_incs1,inc_callref,0);
		if (rowPos == -1)
		{ 		
			boolINCs=true;
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'REQUEST-INCIDENT'))
			{
				strText += "Incident (" + inc_callref_text + ") associated\n";
				
				//-- update known error diary
				strIncText = "The Service Request (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this incident.";
				app.g.new_diaryevent(inc_callref,strIncText,"Service Request Management","Service Request Association");
			}
		}
    } //-- eof for

	//-- create change call diary update then reload list
	if (boolINCs)
	{
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Incident Association");
		_swdoc.EF().sl_incs1.Refresh();
		
		//-- Now check if we should link new ci items as being affect
		if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyIncidentCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var oRes = app.cmdb.get_call_ci_info(strSelectedIncs , "INCIDENT", "");
			if(oRes.keys!="")_swdoc.link_cause_cis_to_change(oRes.keys,oRes.text);
		}		
	}
}

function remove_change_incidents(strCallRefs,boolFromKnownErrorRemoval)
{
	//-- if these incidents are being removed because we removed known errors
	if(boolFromKnownErrorRemoval)
	{
		//-- check if the ke incidents exists in this problem
		var arrayINCS = strCallRefs.split(",");
		for(var x=0;x<arrayINCS.length;x++)
		{
			if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_incs1,arrayINCS[x],0)!=-1)	
			{
				//-- at least ke one incident belongs to this change
				//-- if not confirm exit
				if(!confirm("There are associated incident records, that are linked to the known errors you are removing. Do you want to remove these records as well?"))return;			
				
				//-- if there is at least one break.
				break;	
			}
		}
	}

	if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'REQUEST-INCIDENT'))
	{
		var arrCallrefs = strCallRefs.split(',');
		var strText = "This service request has been dis-associated from the following Incidents\n\n";
		
		for(var x=0;x<arrCallrefs.length;x++)
		{
			strText += "Incident (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";
			
			var strRFCText = "The service request (" + app.g.callref_pad(_swdoc.OC().callref) + ") has been dis-associated from this Incident";
			app.g.new_diaryevent(arrCallrefs[x], strRFCText,"Service Request Management","Service Request Dis-association");
		}
		app.g.new_diaryevent(_swdoc.OC().callref,strText,"Service Request Management","Incident Dis-association");
	}

	_swdoc.EF().sl_incs1.Refresh();	
	_swdoc.EF().sl_incs1_OnItemSelected(0);		
	_swdoc.remove_selected_incident_cis(strCallRefs);			
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_incident_cis(strSelectedIncs)
{
	var array_RemoveImpactCIS = new Array();
	
	if(_swdoc.EF().sl_configitems1.rowCount()<1) return;
	
	//--
	//-- get linked inc cis and see if they are linked to problem
	var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
	if(strLinkedCIs=="0")return;

	var arrayCIS = strLinkedCIs.split(",");
	for(var x=0;x<arrayCIS.length;x++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,arrayCIS[x],0)!=-1)	array_RemoveImpactCIS[array_RemoveImpactCIS.length++] = arrayCIS[x];			
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveImpactCIS.length>0)
	{
		if(confirm("There are associated impact configuration items, that are linked to the incidents you are removing. Do you want to remove these items as well?"))
		{
			//-- unselect all , the select those we want to remove
			app.g.sl_unselect(_swdoc.EF().sl_configitems1);
			for(var y=0;y<array_RemoveImpactCIS.length;y++)
			{
				var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,array_RemoveImpactCIS[y],0);
				if(intRow>-1)
				{
					_swdoc.EF().sl_configitems1.SetRowSelected(intRow, true, true);
				}
			}
			_swdoc.remove_selected_impact_cis();
		}
	}
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_ke_cis(strSelectedKes)
{
	if((_swdoc.EF().sl_configitems1.rowCount()<1)) return;

	_swdoc.remove_selected_ke_causeci(strSelectedKes);
	_swdoc.remove_selected_ke_impactci(strSelectedKes);	
}

function remove_selected_ke_causeci(strSelectedKes)
{	//--
	//-- get linked ke cis and see if they are linked to problem
	var array_RemoveCIS = new Array();
	var strLinkedCauseCIs = app.cmdb.get_call_cis(strSelectedKes , "PROBLEM-CAUSE", "");
	if(strLinkedCauseCIs=="0")return;

	
	var arrayCIS = strLinkedCauseCIs.split(",");
	for(var z=0;z<arrayCIS.length;z++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,arrayCIS[z],0)>-1)
		{
			array_RemoveCIS[array_RemoveCIS.length++] = arrayCIS[z];			
		}
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveCIS.length>0)
	{
		if(confirm("There are associated change configuration items, that are linked to the known errors you are removing. Do you want to remove these items as well?"))
		{
			//-- unselect all , the select those we want to remove
			app.g.sl_unselect(_swdoc.EF().sl_configitems1);
			for(var y=0;y<array_RemoveCIS.length;y++)
			{
				var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,array_RemoveCIS[y],0);
				if(intRow>-1)
				{
					_swdoc.EF().sl_configitems1.SetRowSelected(intRow, true, true);
				}
			}
			_swdoc.remove_selected_cause_cis();
		}
	}
}

function remove_selected_ke_impactci(strSelectedKes)
{	//--
	//-- get linked ke cis and see if they are linked to problem
	var array_RemoveCIS = new Array();
	var strLinkedImpactCIs = app.cmdb.get_call_cis(strSelectedKes , "PROBLEM-AFFECT", "");
	if(strLinkedImpactCIs=="0")return;

	var arrayCIS = strLinkedImpactCIs.split(",");
	for(var z=0;z<arrayCIS.length;z++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,arrayCIS[z],0)!=-1)	
		{
			array_RemoveCIS[array_RemoveCIS.length++] = arrayCIS[z];			
		}
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveCIS.length>0)
	{
		if(confirm("There are associated impact configuration items, that are linked to the known errors you are removing. Do you want to remove these items as well?"))
		{
			//-- unselect all , the select those we want to remove
			app.g.sl_unselect(_swdoc.EF().sl_configitems1);
			for(var y=0;y<array_RemoveCIS.length;y++)
			{
				var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,array_RemoveCIS[y],0);
				if(intRow>-1)
				{
					_swdoc.EF().sl_configitems1.SetRowSelected(intRow, true, true);
				}
			}
			_swdoc.remove_selected_impact_cis();
		}
	}
}

//-- remove selected cause cis
function remove_selected_cause_cis()
{
    //-- NWJ - 15.03.2004
    //-- Remove Selected config items
	var strCIs = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,0,",");
	if(strCIs!= "")
	{
    	//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    	if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strCIs,'REQUEST'))
    	{
    		//-- update incident diary
    		var strCIidentifiers = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,1,",");
			var strText     	= "The following problem configuration items were removed from this service request.\n" + strCIidentifiers;
			var strActionSource = "Service Request Management";
			var strCode			= "Remove Configuration Items";
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    	}			

	    //-- re-filter list	
		_swdoc.EF().sl_configitems1.Refresh();	
		_swdoc.EF().sl_configitems1.SetRowSelected(0);
		_swdoc.EF().sl_configitems1_OnItemSelected(0);
		
    }
}

//-- remove selected impact cis
function remove_selected_impact_cis()
{
    //-- NWJ - 15.03.2004
    //-- Remove Selected config items
	var strCIs = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,0,",");
	if(strCIs!= "")
	{
    	//-- NWJ - 28.02.2006 - Use CMDB object to remove CIs from call
    	if (app.cmdb.unrelate_ci_from_call(_swdoc.OC().callref,strCIs,'REQUEST'))
    	{
    		//-- update incident diary
    		var strCIidentifiers = app.g.sl_getselected_valuesdel(_swdoc.EF().sl_configitems1,1,",");
			var strText     	= "The following impacted configuration items were removed from this service request.\n" + strCIidentifiers;
			var strActionSource = "Service Request Management";
			var strCode			= "Remove Configuration Items";
			app.g.new_dairyevent(_swdoc.OC().callref,strText,strActionSource,strCode);
    	}			

	    //-- re-filter list	
		_swdoc.EF().sl_configitems1.Refresh();	
		_swdoc.EF().sl_configitems1.SetRowSelected(0);
		_swdoc.EF().sl_configitems1_OnItemSelected(0);
    }
}


//-- 29.04.2008 - 2.2.0
function setup_crating()
{
	_swdoc.ib.StartAutoCycle(10000); //-- scroll every 10 seconds
	_swdoc.ib.RemoveItemsByGroup("rating");
	
	app._evi(_swdoc.MF().img_crating,true);
  	var webroot = "&[app.webroot]";

   	if(_swdoc.OC().c_rating==0)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "/clisupp/formimages/itsm/itsm_help.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request not yet rated");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Not yet rated", "", "", "rating");		
  	}
 	
  	if(_swdoc.OC().c_rating==2)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "/clisupp/formimages/itsm/cr_neu16.gif");
		app._ete(_swdoc.MF().lbl_crating,": Request satisfaction Neutral");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Neutral - " + _swdoc.OC().c_ratingtxt, "", "blue", "rating");				
  	}
	if(_swdoc.OC().c_rating==1)
	{
		app._eim(_swdoc.MF().img_crating,webroot+ "/clisupp/formimages/itsm/cr_pos16.gif");
		app._ete(_swdoc.MF().lbl_crating,": Request satisfaction Positive");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Positive - " + _swdoc.OC().c_ratingtxt, "", "green", "rating");						
	}
  	if(_swdoc.OC().c_rating==3)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "/clisupp/formimages/itsm/cr_neg16.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request satisfaction Negative");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Negative - " + _swdoc.OC().c_ratingtxt, "", "red", "rating");								
  	}
  	
	var oUserdbRating = app.g.get_record("userdb_rating",_swdoc.oCust().keysearch,'fk_keysearch');
	if(oUserdbRating!=null)
	{
		app._ete(_swdoc.MF().lbl_open_request,": "+oUserdbRating.active_count);
		app._ete(_swdoc.MF().lbl_closed_request,": "+oUserdbRating.inactive_count);
	}
	
	//-- show overall rating
	switch(_swdoc.oCust().flg_ocrating)
	{
		case 1:
			app._eim(_swdoc.MF().img_orating, webroot + "/clisupp/formimages/itsm/cr_neg16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction negative");
			break;			
		case 2:
			app._eim(_swdoc.MF().img_orating, webroot + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction neutral");
			break;						
		case 3:
		
			app._eim(_swdoc.MF().img_orating, webroot + "/clisupp/formimages/itsm/cr_pos16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction positive");
			break;						
		default:
			app._eim(_swdoc.MF().img_orating, webroot + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction undetermined");
			break;
	}
}

function filter_impact_orgs(oForm)
{
	//-- get list of orgs impacted by impacted customer
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'fk_company_id',",","'",true);
	
	var strOrgIdsFromRFCs = app.g.sl_get_valuesdel(oForm.sl_rfcs,'fk_company_id',",","'",true);
	if(strOrgIdsFromRFCs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIdsFromRFCs;
	}
	
	//-- get orgs that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- get orgs affected by the associated kes
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strOrgIdsFromImpactKes = app.itsm.get_problem_impact_orgids(strKeCallrefs,true);
	if(strOrgIdsFromImpactKes!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIdsFromImpactKes;
	}
	
	if(strOrgFilter=="")strOrgFilter="-1";
	
	app._slf(oForm.sl_orgimpact,"_kv="+strOrgFilter);
	oForm.sl_orgimpact.Refresh();	
}


function setup_request_subtab()
{	
	// DTH: webclient - replaced the original code with the following	
	var boolShowBreach = ((app.g.fix_epoch(_swdoc.OC().fixbyx) < app.global.GetCurrentEpocTime() )&& (!(_swdoc.OC().slafix>0)) || (_swdoc.OC().slafix>0 && _swdoc.OC().withinfix==0));
	_swdoc.MF().TabDesc.ShowTabItem(4,boolShowBreach);

	var boolShowRes = (_swdoc.OC().resolve_text!="")
	_swdoc.MF().TabDesc.ShowTabItem(3,boolShowRes);

	var boolShowClose = (_swdoc.OC().close_text!="" && _swdoc.OC().status>14);
	_swdoc.MF().TabDesc.ShowTabItem(5,boolShowClose);
	
	if (!boolShowBreach && !boolShowRes && !boolShowClose)
	{
		_swdoc.load_diary_textfields(true);
		app._etab(_swdoc.MF().TabDesc,0);
	}
	else
	{
		if (boolShowBreach) {
			app._etab(_swdoc.MF().TabDesc,4);
			// get list of support groups logged in analyst is a member of
			var strAnalystGroups = app.g.get_all_analyst_groups(app.session.analystId);
			var arrAnalystGroups = strAnalystGroups.split(",");
			var index;
			var boolInGroup=false;
			// loop through list of support groups
			for (index in arrAnalystGroups){
				// check if the support group this call is assigned to is in list of groups
				if(arrAnalystGroups[index]==_swdoc.OC().suppgroup){
					boolInGroup=true;
					break;
				}
			}
			/* if logged in analyst is the owner of this call or is the member of
		   	the support group this call is assigned to make the Breach Text field mandatory */
			if(_swdoc.OC().owner==app.session.analystId || boolInGroup) {
				app._ema(_swdoc.MF().tb_breachtext1,true);
			}	
		}
		if (boolShowRes) app._etab(_swdoc.MF().TabDesc,3);
		if (boolShowClose) app._etab(_swdoc.MF().TabDesc,5);
	}
}


function load_diary_textfields(boolLastOnly)
{
	//-- load full diary text into form fields - bug fix 62811
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_last_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_ongoing1);
	if((boolLastOnly==undefined)||(!boolLastOnly))app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext);
}

// DTH: webclient - added the following function so that the Original update can be loaded without loading the Last update
function load_diary_orig()
{
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext);
}

// F0110887 START
// Creating a function to Change the Customer record. This can be called from the Show Me menu and from the Action menu.
// This approach means that we can control access to this action
function search_for_customer(funcCallback)
{
	if(!app.itsm.can_edit(_swdoc.OC().callclass))
	{
		if ((funcCallback != undefined) && (funcCallback != null))
		{
			funcCallback();
		}
	}
	else
	{
		var strURL = "autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCust().fk_company_id;
		app.g.search_for("customers",false,strURL,function(sResults)
		{
			if(sResults.selectedkeys=="")
			{
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
			else
			{
    			var arrKeySearch = sResults.selectedkeys.split(",");
				var arrCompanyId = sResults.selectedcompanyids.split(",");
   			
    			if(arrKeySearch[0]!=_swdoc.oCust().keysearch)
    			{
    				//-- Get Company Info
      				var strParams = "table=userdb&_swc_keysearch=" + app.pfu(arrKeySearch[0]);
					var oRS = app.g.get_sqrecordset("select.tablebycol",strParams);
					if(oRS.Fetch())
					{
						var strOrgID = oRS.GetValueAsString('fk_company_id');
					}
      				var oRS = app.g.get_sqrecordset("company/get_expiry_date","company_id="+strOrgID);
  					if (oRS.Fetch())
  					{ 
      					var intExpiryEpoch = app.g.fix_epoch( app.g.get_field(oRS,"SUPPORTEXPIRYX"));
					}
					var boolOrgSupported = new Boolean(app.g.checkOrgSupport(strOrgID, intExpiryEpoch, true));
					if (boolOrgSupported==true)
					{
    					_swdoc.ChangeRecord("userdb", arrKeySearch[0]);
					}
				}
				if ((funcCallback != undefined) && (funcCallback != null))
				{
					funcCallback();
				}
			}
		});
	}
}
// F0110887 END

						OnDataChanged()
{

	if(_swdoc.intLastOCStatus != _swdoc.OC().status)
	{
		_swdoc.intLastOCStatus = _swdoc.OC().status;
		if(_swdoc.OC().status>15)
		{
			mainform.SetTimer(5005, 100);				
		}
		if(_swdoc.OC().status==6)
		{
			mainform.SetTimer(5005, 100);				
		}
	}

	//-- status has changed
	if(_swdoc.strLastBPM_Status != _swdoc.OC().bpm_status_id)
	{
		_swdoc.strLastBPM_Status = _swdoc.OC().bpm_status_id;			
		//-- have to set time because form does not refresh properly
		if(IsObjectDefined('mainform'))
		{
			mainform.SetTimer(5005, 100);				
		}
	}
	if(_swdoc.OC().bpm_execvpme==1)
	{
		_swdoc.MF().sl_wfauth.Refresh();
	}
	
	//-- in case rating has changed
	_swdoc.setup_crating();
	
	//-- F0088933
	app.g.get_call_last_updatetxt(_swdoc.OC().callref,true,_swdoc.MF().tb_ongoing1);

	//-- F0101293
	if(app.session.analystId==_swdoc.OC().owner)
	{
		if(_swdoc.OC().action_status!="")
			app._een(_swdoc.MF().btn_actionsts, true);
	}
	
}

						OnSaveData()
{
	//--TK Performance Debug
	_swdoc.startTime = new Date().getTime();
	
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return false;

	//-- status has changed
	if(_swdoc.strLastBPM_Status != _swdoc.OC().bpm_status_id)
	{
		if(bpm_stage.flg_requireci==1)
		{
			if(_swdoc.EF().sl_configitems1.rowCount() < 1)
			{
				MessageBox("Before changing the BPM Status for this request you have to associate at least one affected configuration item or service");
				return false;
			}
		}
	}
	return true;
}

						OnChangeCallProfile()
{
	if(!app.itsm.can_edit(_swdoc.OC().callclass,true))return false;
	//-- store orignal
	_swdoc.orig_probcode = _swdoc.OC().probcode;
	return true;
}

						OnRecordChanged(strTable)
{
	if(strTable=="userdb")
	{
		//-- check customer status message
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		
		//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
		if(_swdoc.oCust().fk_company_id != "")
		{
			var recCompany = app.g.get_record("company", _swdoc.oCust().fk_company_id);
			if(recCompany)
			{
				if(recCompany.recordresolvedmsg!="")MessageBox(recCompany.recordresolvedmsg);
			}
			else
			{
				//-- there is adata issue here as the customers organisation does not exist
				MessageBox("The organisation that this customer belongs to cannot be found on the system. Please contact your Supportworks administrator.");
			}
		}
	}
		
}

						OnFormClosing()
{

	if(IsObjectDefined("mainform"))
	{
		//-- NWJ
		//-- remove from openrfcs array
		app.cfg.deallocaterfcdoc(_swdoc.OC().callref);
	}

	app.g.remove_me_document("REQUEST", _swdoc.OC().callref);

	return true;
}

